","keccak256":"0x03ea8d9c47249e9456deba85130e859afd6eba800e330527bcb9bc3438adb6a4","license":"MIT"},"contracts/fast/Crowdfund.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../lib/LibAddressSet.sol";
import "../interfaces/IERC20.sol";
import "../common/AHasMembers.sol";
import "../common/AHasGovernors.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title The `Crowdfund` FAST contract.
 * @notice This contract is used to manage a crowdfunding campaign.
 */
contract Crowdfund {
  using LibAddressSet for LibAddressSet.Data;

  /// @notice Happens when a function requires an unmet phase.
  error InvalidPhase();
  /// @notice Happens when a duplicate entry is found.
  error DuplicateEntry();
  /// @notice Happens when inconsistent parametters are detected.
  error InconsistentParameter(string param);
  /// @notice Happens when an address is not a crowdfund pledger.
  error UnknownPledger(address who);
  /// @notice Happens when a call to the ERC20 token contract fails.
  error TokenContractError();
  /// @notice Happens when there are insufficient funds somewhere.
  error InsufficientFunds(uint256 amount);

  /// @notice Happens when an address is not an issuer member.
  error RequiresIssuerMemberCaller();
  /// @notice Happens when an address is not a FAST member.
  error RequiresFastMemberCaller();
  /// @notice Happens when a parameter has to be a FAST member.
  error RequiresFastMembership(address who);

  /**
   * @notice Emited whenever the internal phase of this crowdfund changes.
   * @param phase The new phase of this contract.
   */
  event Advance(Phase phase);

  /**
   * @notice Emited whenever a plege is made.
   * @param pledger The address of the pledger.
   * @param amount The amount of tokens pledged.
   */
  event Pledge(address indexed pledger, uint256 amount);

  /**
   * @notice Emited when the crowdfunding is terminated.
   * @param success Whether the crowdfunding was successful or not.
   */
  event Terminated(bool indexed success);

  /// @notice The different phases of the crowdfund.
  enum Phase {
    Setup,
    Funding,
    Success,
    Failure
  }

  /// @notice Parameters to be passed to this contract's constructor.
  struct Params {
    /// @notice Who initially deployed this contract.
    address owner;
    /// @notice The issuer contract address.
    address issuer;
    /// @notice The FAST contract that this crowdfund is locked onto.
    address fast;
    /// @notice The beneficiary of the crowdfund.
    address beneficiary;
    /// @notice The fee expressed in basis points - eg ten thousandths.
    uint32 basisPointsFee;
    /// @notice The token contract address.
    IERC20 token;
    /// @notice An arbitrary reference string to keep track of.
    string ref;
  }

  /// @notice A version identifier for us to track what's deployed.
  uint16 public constant VERSION = 1;

  /// @notice The initial params, as passed to the contract's constructor.
  Params private params;
  /// @notice The phase at which the crowdfunding is at.
  Phase public phase;
  /// @notice When was the distribution created.
  uint256 public creationBlock;
  /// @notice How much was collected so far.
  uint256 public collected;

  /// @notice The set of addresses that have pledged to this crowdfund.
  LibAddressSet.Data internal pledgerSet;
  /// @notice The mapping of pledgers to their pledged amounts.
  mapping(address => uint256) public pledges;
  /// @notice Mapping of pledgers to whether they have been refunded or not.
  mapping(address => bool) public refunded;

  /**
   * @notice The constructor for this contract.
   * Note that the constructor places the contract into the setup phase.
   * @param p The parameters to be passed to this contract's constructor.
   */
  constructor(Params memory p) {
    // Store parameters.
    params = p;
    // Check that the owner is a member of the FAST contract.
    if (!isFastMember(p.owner)) revert RequiresFastMembership(p.owner);
    // Check that the beneficiary is a member of the FAST contract.
    else if (!isFastMember(p.beneficiary)) revert RequiresFastMembership(p.beneficiary);
    // Invalid fee - superior than 100%.
    else if (params.basisPointsFee > 10_000) revert InconsistentParameter("basisPointsFee");
    // Keep creation block handy.
    creationBlock = block.number;
  }

  /// @dev Given a total and a fee in basis points, returns the fee amount rounded up.
  function feeAmount() public view returns (uint256) {
    return Math.mulDiv(collected, params.basisPointsFee, 10_000, Math.Rounding.Up);
  }

  /**
   * @notice Advances the campaign to the funding phase.
   * Note that this method is only available during the setup phase.
   */
  function advanceToFunding() external onlyDuring(Phase.Setup) onlyIssuerMember {
    // Make sure the fee doesn't exceed a hundred percent.
    emit Advance(phase = Phase.Funding);
  }

  /**
   * @notice Allows a pledger to pledge tokens to this crowdfund.
   * Note that this method is only available during the funding phase.
   * @param amount The amount of tokens to pledge.
   */
  function pledge(uint256 amount) public onlyDuring(Phase.Funding) onlyFastMember {
    // Make sure the amount is non-zero.
    if (amount == 0) revert InconsistentParameter("amount");
    // Make sure that the message sender gave us allowance for at least this amount.
    uint256 allowance = params.token.allowance(msg.sender, address(this));
    if (allowance < amount) revert InsufficientFunds(amount - allowance);
    // Keep track of the pledger - don't throw if already present.
    pledgerSet.add(msg.sender, true);
    // Add the pledged amount to the existing pledge.
    pledges[msg.sender] += amount;
    // Update the collected amount.
    collected += amount;
    // Transfer the tokens to this contract.
    if (!params.token.transferFrom(msg.sender, address(this), amount)) revert TokenContractError();
    // Emit!
    emit Pledge(msg.sender, amount);
  }

  /**
   * @notice Queries the number of members.
   * @return An `uint256`.
   */
  function pledgerCount() external view returns (uint256) {
    return pledgerSet.values.length;
  }

  /**
   * @notice Queries pages of pledgers based on a start index and a page size.
   * @param index is the offset at which the pagination operation should start.
   * @param perPage is how many items should be returned.
   * @return A `(address[], uint256)` tuple, which first item is the list of addresses and the second item a cursor to the next page.
   */
  function paginatePledgers(uint256 index, uint256 perPage) external view returns (address[] memory, uint256) {
    return LibPaginate.addresses(pledgerSet.values, index, perPage);
  }

  /**
   * @notice Allows an issuer member to terminate the crowdfunding given a success flag.
   * Note that this method is available during any phase and can be used as a panic
   * button to terminate the crowdfunding prematurely.
   * @param success Whether the crowdfunding was successful or not.
   */
  function terminate(bool success) public onlyIssuerMember {
    // If the crowdfunding was successful...
    if (success) {
      // Transfer the fee to the issuer contract if there is one.
      uint256 finalFee = feeAmount();
      if (finalFee > 0)
        if (!params.token.transfer(params.issuer, finalFee)) revert TokenContractError();
      // Transfer the payout to the beneficiary.
      uint256 payout = collected - finalFee;
      // If there's a payout for the beneficiary, transfer it.
      if (payout > 0)
        if (!isFastMember(params.beneficiary))
          // Make sure that the beneficiary is **still** a member of the FAST contract.
          revert RequiresFastMembership(params.beneficiary);
        // Attempt to transfer to the beneficiary.
        else if (!params.token.transfer(params.beneficiary, payout)) revert TokenContractError();
    }
    // Advance to next phase.
    emit Advance(phase = success ? Phase.Success : Phase.Failure);
  }

  /**
   * @notice Allows a pledger to be refunded if the crowdfunding failed.
   * Note that this method is only available during the failure phase.
   * @param pledger The address of the pledger to refund.
   */
  function refund(address pledger) public onlyDuring(Phase.Failure) {
    // Make sure the pledger is in the set.
    if (!pledgerSet.contains(pledger)) revert UnknownPledger(pledger);
    // Pledger has already been refunded...
    else if (refunded[pledger]) revert DuplicateEntry();
    // Track that the pledger has been refunded.
    refunded[pledger] = true;
    // Transfer the tokens to the pledger.
    if (!params.token.transfer(pledger, pledges[pledger])) revert TokenContractError();
  }

  /// Frontend helper functions.

  /**
   * @dev Returns the parameter struct used to construct this contract.
   * @return The parameter struct.
   */
  function paramsStruct() external view returns (Params memory) {
    return params;
  }

  /**
   * @notice Crowdfund details.
   * @dev This struct shouldn't be used in internal storage.
   */
  struct Details {
    uint16 VERSION;
    Crowdfund.Params params;
    Crowdfund.Phase phase;
    uint256 creationBlock;
    uint256 collected;
    uint256 feeAmount;
  }

  /**
   * @notice Gets detailed crowdfund information.
   * @return See: `Details`.
   */
  function details() public view returns (Details memory) {
    return
      Details({
        VERSION: VERSION,
        params: params,
        phase: phase,
        creationBlock: creationBlock,
        collected: collected,
        feeAmount: feeAmount()
      });
  }

  /// Modifiers and ACL functions.

  /**
   * @dev Checks whether the given address is a member of the FAST contract.
   * @param who The address to check.
   * @return A `bool` indicating whether the address is a member of the FAST contract.
   */
  function isFastMember(address who) internal view returns (bool) {
    return AHasMembers(params.fast).isMember(who);
  }

  modifier onlyDuring(Phase _phase) {
    if (_phase != phase) revert InvalidPhase();
    _;
  }

  modifier onlyIssuerMember() {
    if (!AHasMembers(params.issuer).isMember(msg.sender)) revert RequiresIssuerMemberCaller();
    _;
  }

  modifier onlyFastMember() {
    if (!isFastMember(msg.sender)) revert RequiresFastMemberCaller();
    _;
  }
}
","keccak256":"0x7d0b1e5b5fbb13f38c1e5e718e3bc9dada584114bc21b0602a264f28aaa65fa7","license":"MIT"},"contracts/fast/Distribution.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../lib/LibAddressSet.sol";
import "../lib/LibPaginate.sol";
import "../interfaces/IERC20.sol";
import "../common/AHasMembers.sol";
import "../common/AHasAutomatons.sol";
import "./FastAutomatonsFacet.sol";

/**
 * @title The `Distribution` FAST contract.
 * @notice This contract allows for dividends or proceeds to be distributted amongst
 * a list of beneficiaries. It has a lifecycle that can be described based on the
 * following steps (or phases):
 * - Funding, during which the sum to be distributed has to be credited to this contract.
 * - FeeSetup, during which the oracle will define the fee to be paid upon distribution.
 * - BeneficiariesSetup, during which the oracle can setup the beneficiaries.
 * - Withdrawal, during which each beneficiary can withdraw their proceeds.
 * - Terminated, during which nothing is possible.
 */
contract Distribution {
  using LibAddressSet for LibAddressSet.Data;

  /// @notice Happens when a function requires an unmet phase.
  error InvalidPhase();
  /// @notice Happens when a duplicate entry is found.
  error DuplicateEntry();
  /// @notice Happens when inconsistent parametters are detected.
  error InconsistentParameter(string param);
  /// @notice Happens when a call to the ERC20 token contract fails.
  error TokenContractError();
  /// @notice Happens when there are insufficient funds somewhere.
  error InsufficientFunds(uint256 amount);
  /// @notice Happens when the distribution has been overfunded.
  error Overfunded(uint256 amount);
  /// @notice Happens when a beneficiary is not found.
  error UnknownBeneficiary(address who);

  /// @notice Happens when a function must be called by the FAST contract.
  error RequiresFastCaller();
  /// @notice Happens when an address is not crowdfund manager.
  error RequiresManagerCaller();
  /// @notice Happens when a parameter has to be a FAST member.
  error RequiresFastMembership(address who);

  /// @notice The possible phases in which the contract is in.
  enum Phase {
    Funding,
    FeeSetup,
    BeneficiariesSetup,
    Withdrawal,
    Terminated
  }

  /**
   * @notice Emited whenever the internal phase of this distribution changes.
   * @param phase The new phase of this contract.
   */
  event Advance(Phase phase);
  /**
   * @notice Emited whenever a beneficiary is added to the distribution list.
   * @param beneficiary is the address of the beneficiary who was added.
   * @param amount is the amount in native target token that is owed to the beneficiary.
   */
  event BeneficiaryAdded(address indexed beneficiary, uint256 indexed amount);
  /**
   * @notice Emited whenever a beneficiary is removed from the distribution list.
   * @param beneficiary is the address of the beneficiary who was removed.
   */
  event BeneficiaryRemoved(address indexed beneficiary);
  /**
   * @notice Emited whenever a beneficiary withdraws their owings.
   * @param caller is the address who ordered the withdrawal.
   * @param beneficiary is the address of the beneficiary who performed the withdrawal.
   * @param amount is the amount that was withdrawn.
   */
  event Withdrawal(address indexed caller, address indexed beneficiary, uint256 amount);

  /// @notice Parameters to be passed to this contract's constructor.
  struct Params {
    /// @notice The distributor of the distribution - eg the address who ordered its deployment.
    address distributor;
    /// @notice The Issuer contract address.
    address issuer;
    /// @notice To which FAST this Distribution belongs
    address fast;
    /// @notice The target ERC20 address to be distributed to the beneficiaries.
    IERC20 token;
    /// @notice Block latching.
    uint256 blockLatch;
    /// @notice How much is meant to be distributed.
    uint256 total;
    /// @notice An arbitrary reference string.
    string ref;
  }

  /// @notice A version identifier for us to track what's deployed.
  uint16 public constant VERSION = 3;

  /// @notice The initial params, as passed to the contract's constructor.
  Params private params;
  /// @notice The phase at which the distribution is at.
  Phase public phase = Phase.Funding;
  /// @notice When was the distribution created.
  uint256 public creationBlock;
  /// @notice How much the fee that will be distributed to `issuer` is.
  uint256 public fee;
  /// @notice How much is left for distribution.
  uint256 public available;

  /// @notice The list of beneficiaries known to the system.
  LibAddressSet.Data internal beneficiaries;
  /// @notice How much was set asside for a particular beneficiary.
  mapping(address => uint256) public owings;
  /// @notice Whether or not a benificiary has withdrawn yet.
  mapping(address => bool) public withdrawn;

  /**
   * @notice Constructs a new `Distribution` contracts.
   * @param p is a `Params` structure.
   */
  constructor(Params memory p) {
    // If the distribution is latched in the future, throw.
    if (p.blockLatch >= block.number) revert InconsistentParameter("blockLatch");
    // Store all parameters.
    params = p;
    available = p.total;
    creationBlock = block.number;
  }

  function advanceToFeeSetup() public onlyDuring(Phase.Funding) onlyFastCaller {
    // Make sure that the current distribution has exactly the required amount locked.
    uint256 balance = params.token.balanceOf(address(this));
    if (balance != params.total) revert InconsistentParameter("balance");
    // Move to next phase.
    emit Advance(phase = Phase.FeeSetup);
  }

  /**
   * @notice Sets the fee to be taken upon distribution. Only available during the
   * `Phase.FeeSetup` phase, throws otherwise. This method automatically advances the
   * phase to `Phase.BeneficiariesSetup`, so it can only be called once.
   * Note that only a manager (issuer or automaton with the correct privileges) can
   * call this method.
   * @param _fee is the amount that the `issuer` will receive.
   */
  function advanceToBeneficiariesSetup(uint256 _fee) external onlyDuring(Phase.FeeSetup) onlyManager {
    fee = _fee;
    available -= fee;
    // Move to next phase.
    emit Advance(phase = Phase.BeneficiariesSetup);
  }

  /**
   * @notice Advances the distribution to the `Phase.Withdrawal` phase.
   * The distribution must be in the `Phase.BeneficiariesSetup` phase.
   */
  function advanceToWithdrawal() public onlyDuring(Phase.BeneficiariesSetup) onlyManager {
    // If the distribution covers more than the sum of all proceeds, we want
    // to prevent the distribution from advancing to the withdrawal phase.
    if (available > 0) revert Overfunded(available);
    // Transfer the fee to the issuer contract.
    if (!params.token.transfer(params.issuer, fee)) revert TokenContractError();
    // Move to next phase.
    emit Advance(phase = Phase.Withdrawal);
  }

  /**
   * @notice Adds beneficiaries and amounts to the distribution list. Both `_beneficiaries`
   * and `_amounts` arrays must be of the same size, or the method will revert.
   * This method is only available during the `Phase.BeneficiariesSetup` phase.
   * During execution, this method will make sure that the cumulated amounts for all
   * beneficiaries doesn't exceed the `total` amount available for distribution, or it
   * will simply throw.
   * Note that adding the same beneficiary twice will throw.
   * Note that only a manager (issuer or automaton with the correct privileges) can
   * call this method.
   * @param _beneficiaries is the list of beneficiaries to add.
   * @param _amounts is the list of amounts respective to each beneficiary.
   */
  function addBeneficiaries(
    address[] calldata _beneficiaries,
    uint256[] calldata _amounts
  ) public onlyDuring(Phase.BeneficiariesSetup) onlyManager {
    // Beneficiaries and amount sizes must match.
    if (_beneficiaries.length != _amounts.length) revert InconsistentParameter("lengths");

    // We will count how much is needed for all these beneficiaries.
    uint256 needed = 0;
    // For each of the passed beneficiaries...
    for (uint256 i = 0; i < _beneficiaries.length; ) {
      // Memoize a few variables...
      address beneficiary = _beneficiaries[i];
      uint256 amount = _amounts[i];
      // Make sure the beneficiary is a member of the FAST.
      if (!AHasMembers(params.fast).isMember(beneficiary)) revert RequiresFastMembership(beneficiary);

      // Add the beneficiary to our set.
      beneficiaries.add(beneficiary, false);
      // Keep track of the amount this beneficiary is entitled to.
      owings[beneficiary] = amount;
      // Accumulate how much is needed for these beneficiaries.
      needed += amount;
      // Emit!
      emit BeneficiaryAdded(beneficiary, amount);
      // Next iteration.
      unchecked {
        ++i;
      }
    }

    // Make sure that there's enough to pay everyone.
    if (available < needed) revert InsufficientFunds(needed - available);
    // Decrease the amount of available funds.
    unchecked {
      available -= needed;
    }
  }

  /**
   * @notice Removes a list of beneficiaries from the distribution list.
   * Note that removing a non-existent beneficiary will simply throw.
   * During execution, this method will increase the amount available for
   * distribution automatically.
   * Note that only a manager (issuer or automaton with the correct privileges) can
   * call this method.
   * @param _beneficiaries is the list of addresses to remove.
   */
  function removeBeneficiaries(address[] calldata _beneficiaries) external onlyDuring(Phase.BeneficiariesSetup) onlyManager {
    // Remove all specified beneficiaries.
    for (uint256 i = 0; i < _beneficiaries.length; ) {
      address beneficiary = _beneficiaries[i];
      // Remove the beneficiary from our list.
      beneficiaries.remove(beneficiary, false);
      // Increase the amount available for distribution, as it won't go to this beneficiary.
      available += owings[beneficiary];
      // Set the beneficiary's balance to zero.
      owings[beneficiary] = 0;
      // Emit!
      emit BeneficiaryRemoved(beneficiary);
      // Next iteration.
      unchecked {
        ++i;
      }
    }
  }

  /**
   *
   * @notice Returns the number of beneficiaries added so far.
   * @return The count.
   */
  function beneficiaryCount() external view returns (uint256) {
    return beneficiaries.values.length;
  }

  /**
   * @notice Queries pages of beneficiaries based on a start index and a page size.
   * Note that it is possible to query owings for each of these beneficiaries by
   * utilizing the `owings` and `withdrawn` public function.
   * @param index is the offset at which the pagination operation should start.
   * @param perPage is how many items should be returned.
   * @return A `(address[], uint256)` tuple, which first item is the list of addresses and the second item a cursor to the next page.
   */
  function paginateBeneficiaries(uint256 index, uint256 perPage) external view returns (address[] memory, uint256) {
    return LibPaginate.addresses(beneficiaries.values, index, perPage);
  }

  /**
   * @notice This function allows any beneficiary to withdraw what they are owed. This
   * method can only be called during the `Phase.Withdrawal` phase.
   * Note that this function is protected from reentrancy as it operates on the `token`
   * methods.
   */
  function withdraw(address beneficiary) public onlyDuring(Phase.Withdrawal) {
    if (!beneficiaries.contains(beneficiary)) revert UnknownBeneficiary(beneficiary);
    else if (withdrawn[beneficiary]) revert DuplicateEntry();
    // Memoize a few variables.
    uint256 amount = owings[beneficiary];
    // Make sure they can't do it again later... It is important
    // to do this before any call to `token` to prevent reentrancy.
    withdrawn[beneficiary] = true;
    // Transfer to the beneficiary all of their ownings.
    if (!params.token.transfer(beneficiary, amount)) revert TokenContractError();
    // Emit!
    emit Withdrawal(msg.sender, beneficiary, amount);
  }

  /**
   * @notice A panic function that can only be called by the distribution manager
   * (either an issuer member or an automaton with the right privileges).
   * Upon calling this method, the contract will simply send back any funds still
   * available to it and set its internal state to a termination one.
   * Note that since this method calls the `token` contract, it **must be
   * protected against reentrancy**.
   */
  function terminate() public onlyManager exceptDuring(Phase.Terminated) {
    // Reset internal variables so that it's clear that the contract is terminated.
    // It is important to do this prior to any call to `token` methods to prevent
    // re-entrancy attacks.
    emit Advance(phase = Phase.Terminated);
    available = 0;
    // Move all funds to the distributor account.
    params.token.transfer(params.distributor, params.token.balanceOf(address(this)));
  }

  /// Frontend helpers.

  /**
   * @dev Returns the parameter struct used to construct this contract.
   * @return The parameter struct.
   */
  function paramsStruct() external view returns (Params memory) {
    return params;
  }

  /**
   * @notice Distribution details.
   * @dev This struct shouldn't be used in internal storage.
   */
  struct Details {
    uint16 VERSION;
    Distribution.Params params;
    Distribution.Phase phase;
    uint256 creationBlock;
    uint256 fee;
    uint256 available;
  }

  /**
   * @notice Gets detailed distribution information.
   * @return See: `Details`.
   */
  function details() public view returns (Details memory) {
    return
      Details({
        VERSION: VERSION,
        params: params,
        phase: phase,
        creationBlock: creationBlock,
        fee: fee,
        available: available
      });
  }

  /// Modifiers.

  modifier onlyDuring(Phase _phase) {
    if (_phase != phase) revert InvalidPhase();
    _;
  }

  modifier exceptDuring(Phase _phase) {
    if (_phase == phase) revert InvalidPhase();
    _;
  }

  modifier onlyFastCaller() {
    if (msg.sender != params.fast) revert RequiresFastCaller();
    _;
  }

  modifier onlyManager() {
    if (
      !AHasMembers(params.issuer).isMember(msg.sender) &&
      !AHasAutomatons(params.fast).automatonCan(msg.sender, FAST_PRIVILEGE_MANAGE_DISTRIBUTIONS)
    ) revert RequiresManagerCaller();
    _;
  }
}
