pragma solidity 0.8.10;

import '../lib/LibAddressSet.sol';
import '../interfaces/IERC20.sol';
import '../common/AHasMembers.sol';
import '../common/AHasGovernors.sol';
import '@openzeppelin/contracts/utils/math/Math.sol';


/**
 * @title The `Crowdfund` FAST contract.
 * @notice This contract is used to manage a crowdfunding campaign.
 */
contract Crowdfund {
  using LibAddressSet for LibAddressSet.Data;

  /// @notice Happens when a function requires an unmet phase.
  error InvalidPhase();
  /// @notice Happens when a duplicate entry is found.
  error DuplicateEntry();
  /// @notice Happens when inconsistent parametters are detected.
  error InconsistentParameter(string param);
  /// @notice Happens when an address is not a crowdfund pledger.
  error UnknownPledger(address who);
  /// @notice Happens when a call to the ERC20 token contract fails.
  error TokenContractError();
  /// @notice Happens when there are insufficient funds somewhere.
  error InsufficientFunds(uint256 amount);

  /// @notice Happens when an address is not an issuer member.
  error RequiresIssuerMemberCaller();
  /// @notice Happens when an address is not a FAST member.
  error RequiresFastMemberCaller();
  /// @notice Happens when a parameter has to be a FAST governor.
  error RequiresFastGovernorship(address who);
  /// @notice Happens when a parameter has to be a FAST member.
  error RequiresFastMembership(address who);

  /**
   * @notice Emited whenever the internal phase of this crowdfund changes.
   * @param phase The new phase of this contract.
   */
  event Advance(Phase phase);

  /**
   * @notice Emited whenever a plege is made.
   * @param pledger The address of the pledger.
   * @param amount The amount of tokens pledged.
   */
  event Pledge(address indexed pledger, uint256 amount);

  /**
   * @notice Emited when the crowdfunding is terminated.
   * @param success Whether the crowdfunding was successful or not.
   */
  event Terminated(bool indexed success);

  /// @notice The different phases of the crowdfund.
  enum Phase { Setup, Funding, Success, Failure }

  /// @notice Parameters to be passed to this contract's constructor.
  struct Params {
    /// @notice Who initially deployed this contract.
    address owner;
    /// @notice The issuer contract address.
    address issuer;
    /// @notice The FAST contract that this crowdfund is locked onto.
    address fast;
    /// @notice The beneficiary of the crowdfund.
    address beneficiary;
    /// @notice The token contract address.
    IERC20 token;
    /// @notice An arbitrary reference string to keep track of.
    string ref;
  }

  /// @notice A version identifier for us to track what's deployed.
  uint16 public constant VERSION = 1;

  /// @notice The initial params, as passed to the contract's constructor.
  Params public params;
  /// @notice The phase at which the crowdfunding is at.
  Phase public phase;
  /// @notice When was the distribution created.
  uint256 public creationBlock;
  /// @notice The fee expressed in basis points - eg ten thousandths.
  uint256 public basisPointsFee;
  /// @notice How much was collected so far.
  uint256 public collected;

  /// @notice The set of addresses that have pledged to this crowdfund.
  LibAddressSet.Data internal pledgerSet;
  /// @notice The mapping of pledgers to their pledged amounts.
  mapping(address => uint256) public pledges;
  /// @notice Mapping of pledgers to whether they have been refunded or not.
  mapping(address => bool) public refunded;

  /**
   * @notice The constructor for this contract.
   * Note that the constructor places the contract into the setup phase.
   * @param p The parameters to be passed to this contract's constructor.
   */
  constructor(Params memory p) {
    // Store parameters.
    params = p;
    // Check that the owner is a member of the FAST contract.
    if (!isFastGovernor(p.owner))
      revert RequiresFastGovernorship(p.owner);
    // Check that the beneficiary is a member of the FAST contract.
    else if (!isFastMember(p.beneficiary))
      revert RequiresFastMembership(p.beneficiary);
    // Keep creation block handy.
    creationBlock = block.number;
  }

  /// @dev Given a total and a fee in basis points, returns the fee amount rounded up.
  function feeAmount()
      public view returns(uint256) {
    return Math.mulDiv(collected, basisPointsFee, 10_000, Math.Rounding.Up);
  }

  /**
   * @notice Advances the campaign to the funding phase.
   * Note that this method is only available during the setup phase.
   * @param _basisPointsFee The fee expressed in basis points - eg ten thousandths.
   */
  function advanceToFunding(uint256 _basisPointsFee)
      external onlyDuring(Phase.Setup) onlyIssuerMember {
    // Make sure the fee doesn't exceed a hundred percent.
    if (_basisPointsFee > 10_000)
      revert InconsistentParameter("basisPointsFee");
    basisPointsFee = _basisPointsFee;
    emit Advance(phase = Phase.Funding);
  }

  /**
   * @notice Allows a pledger to pledge tokens to this crowdfund.
   * Note that this method is only available during the funding phase.
   * @param amount The amount of tokens to pledge.
   */
  function pledge(uint256 amount)
      public onlyDuring(Phase.Funding) onlyFastMember {
    // Make sure the amount is non-zero.
    if (amount == 0)
      revert InconsistentParameter("amount");
    // Make sure that the message sender gave us allowance for at least this amount.
    uint256 allowance = params.token.allowance(msg.sender, address(this));
    if (allowance < amount)
      revert InsufficientFunds(amount - allowance);
    // Keep track of the pledger - don't throw if already present.
    pledgerSet.add(msg.sender, true);
    // Add the pledged amount to the existing pledge.
    pledges[msg.sender] += amount;
    // Update the collected amount.
    collected += amount;
    // Transfer the tokens to this contract.
    if (!params.token.transferFrom(msg.sender, address(this), amount))
      revert TokenContractError();
    // Emit!
    emit Pledge(msg.sender, amount);
  }

  /**
   * @notice Queries the number of members.
   * @return An `uint256`.
   */
  function pledgerCount()
      external view returns(uint256) {
    return pledgerSet.values.length;
  }

  /**
   * @notice Queries pages of pledgers based on a start index and a page size.
   * @param index is the offset at which the pagination operation should start.
   * @param perPage is how many items should be returned.
   * @return A `(address[], uint256)` tuple, which first item is the list of addresses and the second item a cursor to the next page.
   */
  function paginatePledgers(uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(pledgerSet.values, index, perPage);
  }

  /**
   * @notice Allows an issuer member to terminate the crowdfunding given a success flag.
   * Note that this method is available during any phase and can be used as a panic
   * button to terminate the crowdfunding prematurely.
   * @param success Whether the crowdfunding was successful or not.
   */
  function terminate(bool success)
      public onlyIssuerMember {
    // If the crowdfunding was successful...
    if (success) {
      // Transfer the fee to the issuer contract if there is one.
      uint256 finalFee = feeAmount();
      if (finalFee > 0)
        if (!params.token.transfer(params.issuer, finalFee))
          revert TokenContractError();
      // Transfer the payout to the beneficiary.
      uint256 payout = collected - finalFee;
      // If there's a payout for the beneficiary, transfer it.
      if (payout > 0)
        // Make sure that the beneficiary is **still** a member of the FAST contract.
        if (!isFastMember(params.beneficiary))
          revert RequiresFastMembership(params.beneficiary);
        // Attempt to transfer to the beneficiary.
        else if (!params.token.transfer(params.beneficiary, payout))
          revert TokenContractError();
    }
    // Advance to next phase.
    emit Advance(phase = success ? Phase.Success : Phase.Failure);
  }

  /**
   * @notice Allows a pledger to be refunded if the crowdfunding failed.
   * Note that this method is only available during the failure phase.
   * @param pledger The address of the pledger to refund.
   */
  function refund(address pledger)
      public onlyDuring(Phase.Failure) {
    // Make sure the pledger is in the set.
    if (!pledgerSet.contains(pledger))
      revert UnknownPledger(pledger);
    // Pledger has already been refunded...
    else if (refunded[pledger])
      revert DuplicateEntry();
    // Track that the pledger has been refunded.
    refunded[pledger] = true;
    // Transfer the tokens to the pledger.
    if (!params.token.transfer(pledger, pledges[pledger]))
      revert TokenContractError();
  }

  /// Modifiers and ACL functions.

  /**
   * @notice Checks whether the given address is a governor of the FAST contract.
   * @param who The address to check.
   * @return A `bool` indicating whether the address is a governor of the FAST contract.
   */
  function isFastGovernor(address who)
      internal view returns(bool) {
    return AHasGovernors(params.fast).isGovernor(who);
  }

  /**
   * @dev Checks whether the given address is a member of the FAST contract.
   * @param who The address to check.
   * @return A `bool` indicating whether the address is a member of the FAST contract.
   */
  function isFastMember(address who)
      internal view returns(bool) {
    return AHasMembers(params.fast).isMember(who);
  }

  modifier onlyDuring(Phase _phase) {
    if (_phase != phase)
      revert InvalidPhase();
    _;
  }

  modifier onlyIssuerMember() {
    if (!AHasMembers(params.issuer).isMember(msg.sender))
      revert RequiresIssuerMemberCaller();
    _;
  }

  modifier onlyFastMember() {
    if (!isFastMember(msg.sender))
      revert RequiresFastMemberCaller();
    _;
  }
}
","keccak256":"0x78f998617d8daa0c22846857a5bd8ae2102a2441816769cef8d022bd43c1aaca","license":"MIT"},"contracts/fast/Distribution.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import '../interfaces/IERC20.sol';
import '../common/AHasMembers.sol';
import '../common/AHasAutomatons.sol';
import './FastAutomatonsFacet.sol';


/**
 * @title The `Distribution` FAST contract.
 * @notice This contract allows for dividends or proceeds to be distributted amongst
 * a list of beneficiaries. It has a lifecycle that can be described based on the
 * following steps (or phases):
 * - Funding, during which the sum to be distributed has to be credited to this contract.
 * - FeeSetup, during which the oracle will define the fee to be paid upon distribution.
 * - BeneficiariesSetup, during which the oracle can setup the beneficiaries.
 * - Withdrawal, during which each beneficiary can withdraw their proceeds.
 * - Terminated, during which nothing is possible.
 */
contract Distribution {
  using LibAddressSet for LibAddressSet.Data;

  /// @notice Happens when a function requires an unmet phase.
  error InvalidPhase();
  /// @notice Happens when a duplicate entry is found.
  error DuplicateEntry();
  /// @notice Happens when inconsistent parametters are detected.
  error InconsistentParameter(string param);
  /// @notice Happens when a call to the ERC20 token contract fails.
  error TokenContractError();
  /// @notice Happens when there are insufficient funds somewhere.
  error InsufficientFunds(uint256 amount);
  /// @notice Happens when the distribution has been overfunded.
  error Overfunded(uint256 amount);
  /// @notice Happens when a beneficiary is not found.
  error UnknownBeneficiary(address who);

  /// @notice Happens when a function must be called by the FAST contract.
  error RequiresFastCaller();
  /// @notice Happens when an address is not crowdfund manager.
  error RequiresManagerCaller();
  /// @notice Happens when a parameter has to be a FAST member.
  error RequiresFastMembership(address who);

  /// @notice The possible phases in which the contract is in.
  enum Phase { Funding, FeeSetup, BeneficiariesSetup, Withdrawal, Terminated }

  /**
   * @notice Emited whenever the internal phase of this distribution changes.
   * @param phase The new phase of this contract.
   */
  event Advance(Phase phase);
  /**
   * @notice Emited whenever a beneficiary is added to the distribution list.
   * @param beneficiary is the address of the beneficiary who was added.
   * @param amount is the amount in native target token that is owed to the beneficiary. 
   */
  event BeneficiaryAdded(address indexed beneficiary, uint256 indexed amount);
  /**
   * @notice Emited whenever a beneficiary is removed from the distribution list.
   * @param beneficiary is the address of the beneficiary who was removed.
   */
  event BeneficiaryRemoved(address indexed beneficiary);
  /**
   * @notice Emited whenever a beneficiary withdraws their owings.
   * @param caller is the address who ordered the withdrawal.
   * @param beneficiary is the address of the beneficiary who performed the withdrawal.
   * @param amount is the amount that was withdrawn.
   */
  event Withdrawal(address indexed caller, address indexed beneficiary, uint256 amount);

  /// @notice Parameters to be passed to this contract's constructor.
  struct Params {
    /// @notice The distributor of the distribution - eg the address who ordered its deployment.
    address distributor;
    /// @notice The Issuer contract address.
    address issuer;
    /// @notice To which FAST this Distribution belongs
    address fast;
    /// @notice The target ERC20 address to be distributed to the beneficiaries.
    IERC20 token;
    /// @notice Block latching.
    uint256 blockLatch;
    /// @notice How much is meant to be distributed.
    uint256 total;
    /// @notice An arbitrary reference string.
    string ref;
  }

  /// @notice A version identifier for us to track what's deployed.
  uint16 public constant VERSION = 3;

  /// @notice The initial params, as passed to the contract's constructor.
  Params public params;
  /// @notice The phase at which the distribution is at.
  Phase public phase = Phase.Funding;
  /// @notice When was the distribution created.
  uint256 public creationBlock;
  /// @notice How much the fee that will be distributed to `issuer` is.
  uint256 public fee;
  /// @notice How much is left for distribution.
  uint256 public available;

  /// @notice The list of beneficiaries known to the system.
  LibAddressSet.Data internal beneficiaries;
  /// @notice How much was set asside for a particular beneficiary.
  mapping(address => uint256) public owings;
  /// @notice Whether or not a benificiary has withdrawn yet.
  mapping(address => bool) public withdrawn;

  /**
   * @notice Constructs a new `Distribution` contracts.
   * @param p is a `Params` structure.
   */
  constructor(Params memory p) {
    // If the distribution is latched in the future, throw.
    if (p.blockLatch >= block.number)
      revert InconsistentParameter("blockLatch");
    // Store all parameters.
    params = p;
    available = p.total;
    creationBlock = block.number;
  }

  function advanceToFeeSetup()
      public onlyDuring(Phase.Funding) onlyFastCaller {
    // Make sure that the current distribution has exactly the required amount locked.
    uint256 balance = params.token.balanceOf(address(this));
    if (balance != params.total)
      revert InconsistentParameter("balance");
    // Move to next phase.
    emit Advance(phase = Phase.FeeSetup);
  }

  /**
   * @notice Sets the fee to be taken upon distribution. Only available during the
   * `Phase.FeeSetup` phase, throws otherwise. This method automatically advances the
   * phase to `Phase.BeneficiariesSetup`, so it can only be called once.
   * Note that only a manager (issuer or automaton with the correct privileges) can
   * call this method.
   * @param _fee is the amount that the `issuer` will receive.
   */
  function advanceToBeneficiariesSetup(uint256 _fee)
      external onlyDuring(Phase.FeeSetup) onlyManager {
    fee = _fee;
    available -= fee;
    // Move to next phase.
    emit Advance(phase = Phase.BeneficiariesSetup);
  }

  /**
   * @notice Advances the distribution to the `Phase.Withdrawal` phase.
   * The distribution must be in the `Phase.BeneficiariesSetup` phase.
   */
  function advanceToWithdrawal()
      public onlyDuring(Phase.BeneficiariesSetup) onlyManager {
    // If the distribution covers more than the sum of all proceeds, we want
    // to prevent the distribution from advancing to the withdrawal phase.
    if (available > 0)
      revert Overfunded(available);
    // Transfer the fee to the issuer contract.
    if (!params.token.transfer(params.issuer, fee))
      revert TokenContractError();
    // Move to next phase.
    emit Advance(phase = Phase.Withdrawal);
  }

  /**
   * @notice Adds beneficiaries and amounts to the distribution list. Both `_beneficiaries`
   * and `_amounts` arrays must be of the same size, or the method will revert.
   * This method is only available during the `Phase.BeneficiariesSetup` phase.
   * During execution, this method will make sure that the cumulated amounts for all
   * beneficiaries doesn't exceed the `total` amount available for distribution, or it
   * will simply throw.
   * Note that adding the same beneficiary twice will throw.
   * Note that only a manager (issuer or automaton with the correct privileges) can
   * call this method.
   * @param _beneficiaries is the list of beneficiaries to add.
   * @param _amounts is the list of amounts respective to each beneficiary.
   */
  function addBeneficiaries(address[] calldata _beneficiaries, uint256[] calldata _amounts)
      public onlyDuring(Phase.BeneficiariesSetup) onlyManager {
    // Beneficiaries and amount sizes must match.
    if (_beneficiaries.length != _amounts.length)
      revert InconsistentParameter("lengths");

    // We will count how much is needed for all these beneficiaries.
    uint256 needed = 0;
    // For each of the passed beneficiaries...
    for (uint256 i = 0; i < _beneficiaries.length;) {
      // Memoize a few variables...
      address beneficiary = _beneficiaries[i];
      uint256 amount = _amounts[i];
      // Make sure the beneficiary is a member of the FAST.
      if (!AHasMembers(params.fast).isMember(beneficiary))
        revert RequiresFastMembership(beneficiary);

      // Add the beneficiary to our set.
      beneficiaries.add(beneficiary, false);
      // Keep track of the amount this beneficiary is entitled to.
      owings[beneficiary] = amount;
      // Accumulate how much is needed for these beneficiaries.
      needed += amount;
      // Emit!
      emit BeneficiaryAdded(beneficiary, amount);
      // Next iteration.
      unchecked { ++i; }
    }

    // Make sure that there's enough to pay everyone.
    if (available < needed)
      revert InsufficientFunds(needed - available);
    // Decrease the amount of available funds.
    unchecked { available -= needed; }
  }

  /**
   * @notice Removes a list of beneficiaries from the distribution list.
   * Note that removing a non-existent beneficiary will simply throw.
   * During execution, this method will increase the amount available for
   * distribution automatically.
   * Note that only a manager (issuer or automaton with the correct privileges) can
   * call this method.
   * @param _beneficiaries is the list of addresses to remove.
   */
  function removeBeneficiaries(address[] calldata _beneficiaries)
      external onlyDuring(Phase.BeneficiariesSetup) onlyManager {
    // Remove all specified beneficiaries.
    for (uint256 i = 0; i < _beneficiaries.length;) {
      address beneficiary = _beneficiaries[i];
      // Remove the beneficiary from our list.
      beneficiaries.remove(beneficiary, false);
      // Increase the amount available for distribution, as it won't go to this beneficiary.
      available += owings[beneficiary];
      // Set the beneficiary's balance to zero.
      owings[beneficiary] = 0;
      // Emit!
      emit BeneficiaryRemoved(beneficiary);
      // Next iteration.
      unchecked { ++i; }
    }
  }

  /**
   * 
   * @notice Returns the number of beneficiaries added so far.
   * @return The count.
   */
  function beneficiaryCount()
      external view returns(uint256) {
    return beneficiaries.values.length;
  }

  /**
   * @notice Queries pages of beneficiaries based on a start index and a page size.
   * Note that it is possible to query owings for each of these beneficiaries by
   * utilizing the `owings` and `withdrawn` public function.
   * @param index is the offset at which the pagination operation should start.
   * @param perPage is how many items should be returned.
   * @return A `(address[], uint256)` tuple, which first item is the list of addresses and the second item a cursor to the next page.
   */
  function paginateBeneficiaries(uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      beneficiaries.values,
      index,
      perPage
    );
  }

  /**
   * @notice This function allows any beneficiary to withdraw what they are owed. This
   * method can only be called during the `Phase.Withdrawal` phase.
   * Note that this function is protected from reentrancy as it operates on the `token`
   * methods.
   */
  function withdraw(address beneficiary)
      public onlyDuring(Phase.Withdrawal) {
    if (!beneficiaries.contains(beneficiary))
      revert UnknownBeneficiary(beneficiary);
    else if (withdrawn[beneficiary])
      revert DuplicateEntry();
    // Memoize a few variables.
    uint256 amount = owings[beneficiary];
    // Make sure they can't do it again later... It is important
    // to do this before any call to `token` to prevent reentrancy.
    withdrawn[beneficiary] = true;
    // Transfer to the beneficiary all of their ownings.
    if (!params.token.transfer(beneficiary, amount))
      revert TokenContractError();
    // Emit!
    emit Withdrawal(msg.sender, beneficiary, amount);
  }

  /**
   * @notice A panic function that can only be called by the distribution manager
   * (either an issuer member or an automaton with the right privileges).
   * Upon calling this method, the contract will simply send back any funds still
   * available to it and set its internal state to a termination one.
   * Note that since this method calls the `token` contract, it **must be
   * protected against reentrancy**.
   */
  function terminate()
      public onlyManager exceptDuring(Phase.Terminated) {
    // Reset internal variables so that it's clear that the contract is terminated.
    // It is important to do this prior to any call to `token` methods to prevent
    // re-entrancy attacks.
    emit Advance(phase = Phase.Terminated);
    available = 0;
    // Move all funds to the distributor account.
    params.token.transfer(params.distributor, params.token.balanceOf(address(this)));
  }

  /// Modifiers.

  modifier onlyDuring(Phase _phase) {
    if (_phase != phase)
      revert InvalidPhase();
    _;
  }

  modifier exceptDuring(Phase _phase) {
    if (_phase == phase)
      revert InvalidPhase();
    _;
  }

  modifier onlyFastCaller() {
    if (msg.sender != params.fast)
      revert RequiresFastCaller();
    _;
  }

  modifier onlyManager() {
    if (!AHasMembers(params.issuer).isMember(msg.sender) &&
        !AHasAutomatons(params.fast).automatonCan(msg.sender, FAST_PRIVILEGE_MANAGE_DISTRIBUTIONS))
      revert RequiresManagerCaller();
    _;
  }
}
","keccak256":"0x9af973041d886a3e8245aa6007b041bed47337bc587aa0ae51cc53edfdc5bb78","license":"MIT"},"contracts/fast/FastAccessFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import '../common/AHasMembers.sol';
import '../common/AHasGovernors.sol';
import '../marketplace/MarketplaceAccessFacet.sol';
import '../issuer/IssuerAccessFacet.sol';
import './FastTokenFacet.sol';
import './lib/AFastFacet.sol';
import './lib/LibFast.sol';
import './FastTopFacet.sol';
import './FastFrontendFacet.sol';
import './FastAutomatonsFacet.sol';


/**
 * @title The Fast Smart Contract.
 * @notice The FAST Access facet is the source of truth when it comes to
 * permissioning and ACLs within a given FAST.
 */
contract FastAccessFacet is AFastFacet, AHasGovernors, AHasMembers {
  using LibAddressSet for LibAddressSet.Data;
  /// Structs.

  /**
   * @notice This structure isn't used anywhere in storage. Instead, it
   * allows various methods of the contract to return all the flags
   * associated with a given address in one go.
   */
  struct Flags {
    /// @notice Whether or not the item in scope is considered a governor of this FAST.
    bool isGovernor;
    /// @notice Whether or not the item in scope is considered a member of this FAST.
    bool isMember;
  }

  /// AHasGovernors implementation.

  function isGovernorsManager(address who)
      internal view override(AHasGovernors) returns(bool) {
    return _isIssuerMember(who);
  }

  function isValidGovernor(address who)
      internal view override(AHasGovernors) returns(bool) {
    return _isMarketplaceMember(who) && AHasMembers(address(this)).isMember(who);
  }

  function onGovernorAdded(address governor)
      internal override(AHasGovernors) {
    // Notify issuer that this governor was added to this FAST.
    IssuerAccessFacet(LibFast.data().issuer).governorAddedToFast(governor);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  function onGovernorRemoved(address governor)
      internal override(AHasGovernors) {
    // Notify issuer that this governor was removed from this FAST.
    IssuerAccessFacet(LibFast.data().issuer).governorRemovedFromFast(governor);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  /// AHasMembers implementation.

  function isMembersManager(address who)
      internal view override(AHasMembers) returns(bool) {
    return
      AHasGovernors(this).isGovernor(who) ||
      AHasAutomatons(address(this)).automatonCan(who, FAST_PRIVILEGE_MANAGE_MEMBERS);
  }

  function isValidMember(address who)
      internal view override(AHasMembers) returns(bool) {
    return _isMarketplaceMember(who);
  }

  function onMemberAdded(address member)
      internal override(AHasMembers) {
    // Notify marketplace that this member was added to this FAST.
    MarketplaceAccessFacet(LibFast.data().marketplace).memberAddedToFast(member);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  function onMemberRemoved(address member)
      internal override(AHasMembers) {
    // Notify token facet that this member was removed.
    FastTokenFacet(address(this)).beforeRemovingMember(member);
    // Notify marketplace that this member was removed from this FAST.
    MarketplaceAccessFacet(LibFast.data().marketplace).memberRemovedFromFast(member);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  /// Flags.

  /**
   * @notice Retrieves flags for a given address.
   * @param a is the address to retrieve flags for.
   * @return A `Flags` struct.
   */
  function flags(address a)
      external view returns(Flags memory) {
    return Flags({
        isGovernor: AHasGovernors(address(this)).isGovernor(a),
        isMember: AHasMembers(address(this)).isMember(a)
      });
  }
}
","keccak256":"0xb48c40f83257dddf44c963e90a56224055a41d777e89e4f9e85e90a0b5c67162","license":"MIT"},"contracts/fast/FastAutomatonsFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import './lib/AFastFacet.sol';
import '../common/AHasAutomatons.sol';


// Privileges bits.
uint32 constant FAST_PRIVILEGE_MANAGE_MEMBERS = 1;
uint32 constant FAST_PRIVILEGE_MANAGE_DISTRIBUTIONS = 2;

/**
 * @title The Fast Smart Contract.
 * @notice The Fast Automatons facet is in charge of keeping track of automaton accounts.
 */
contract FastAutomatonsFacet is AFastFacet, AHasAutomatons {
  /// Automatons management.

  function isAutomatonsManager(address who)
      internal view override(AHasAutomatons)
      returns(bool) {
    return _isIssuerMember(who);
  }
}
","keccak256":"0xb7c24385b1896137f01fb2d7edf20d0d8b301324c4825f51755a40b46bbd15ab","license":"MIT"},"contracts/fast/FastFrontendFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibHelpers.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastToken.sol';


/**
 * @notice A facet dedicated to view / UI only methods. This facet should never hold any method that
 * is not either `pure` or `view`, except to emit events.
 */
contract FastFrontendFacet is AFastFacet {
  using LibAddressSet for LibAddressSet.Data;

  /// Data structures.

  /**
   * @notice This struct groups the common attributes of a FAST.
   * @dev This struct shouldn't be used in internal storage.
   */
  struct Details {
    /// @notice The `address` of the FAST.
    address addr;
    /// @notice The `name` of the FAST (ERC20).
    string name;
    /// @notice The `symbol` of the FAST (ERC20).
    string symbol;
    /// @notice The `decimals` of the FAST (ERC20).
    uint8 decimals;
    /// @notice The `totalSupply` of the FAST (ERC20).
    uint256 totalSupply;
    /// @notice Whether the FAST is semi public or not.
    bool isSemiPublic;
    /// @notice Whether the FAST has a fixed supply or continious.
    bool hasFixedSupply;
    /// @notice Whether the transfers are enabled or not for this FAST.
    bool transfersDisabled;
    /// @notice The reserve balance.
    uint256 reserveBalance;
    /// @notice The number of members the FAST has.
    uint256 memberCount;
    /// @notice The number of governors for the FAST.
    uint256 governorCount;
  }

  /**
   * @notice Governor level details.
   * @dev Note that **this struct shouldn't be used in internal storage**.
   */
  struct GovernorDetails {
    /// @notice The Governor's address.
    address addr;
    /// @notice Whether the Governor is also a Member.
    bool isMember;
  }

  /**
   * @notice Member level details.
   * @dev This struct shouldn't be used in internal storage.
   */
  struct MemberDetails {
    /// @notice The Member's address.
    address addr;
    /// @notice The Member's balance.
    uint256 balance;
    /// @notice Whether the Member is also a Governor.
    bool isGovernor;
  }

  /// Emitters.

  /**
   * @notice Called by diamond facets, signals that FAST details may have changed.
   * 
   * Business logic:
   * - Modifiers:
   *   - Requires the caller to be another facet of the diamond.
   * Emits `DetailsChanged`, see `IFastEvents.DetailsChanged`
   */
  function emitDetailsChanged()
      external onlyDiamondFacet {
    LibFastToken.Data storage tokenData = LibFastToken.data();
    emit DetailsChanged({
      transfersDisabled: LibFast.data().transfersDisabled,
      memberCount: LibHasMembers.data().memberSet.values.length,
      governorCount: LibHasGovernors.data().governorSet.values.length,
      totalSupply: tokenData.totalSupply,
      reserveBalance: tokenData.balances[LibHelpers.ZERO_ADDRESS]
    });
  }

  /// Public functions.

  /**
   * @notice Gets the details of a FAST.
   * @return The details for the current FAST, see `Details`.
   */
  function details()
      public view returns(Details memory) {
    LibFast.Data storage topStorage = LibFast.data();
    LibFastToken.Data storage tokenStorage = LibFastToken.data();
    return Details({
      addr: address(this),
      name: tokenStorage.name,
      symbol: tokenStorage.symbol,
      decimals: tokenStorage.decimals,
      totalSupply: tokenStorage.totalSupply,
      isSemiPublic: topStorage.isSemiPublic,
      hasFixedSupply: topStorage.hasFixedSupply,
      transfersDisabled: topStorage.transfersDisabled,
      reserveBalance: tokenStorage.balances[LibHelpers.ZERO_ADDRESS],
      memberCount: AHasMembers(address(this)).memberCount(),
      governorCount: LibHasGovernors.data().governorSet.values.length
    });
  }

  /**
   * @notice Gets detailed governor details.
   * @return GovernorDetails See: `GovernorDetails`.
   */
  function detailedGovernor(address governor)
      public view returns(GovernorDetails memory) {
    return GovernorDetails({
      addr: governor,
      isMember: AHasMembers(address(this)).isMember(governor)
    });
  }

  function paginateDetailedGovernors(uint256 index, uint256 perPage)
      external view returns(GovernorDetails[] memory, uint256) {
    (address[] memory governors, uint256 nextCursor) =
      LibPaginate.addresses(LibHasGovernors.data().governorSet.values, index, perPage);
    GovernorDetails[] memory values = new GovernorDetails[](governors.length);
    uint256 length =  governors.length;
    for (uint256 i = 0; i < length;) {
      values[i] = detailedGovernor(governors[i]);
      unchecked { ++i; }
    }
    return (values, nextCursor);
  }

  /**
   * @notice Gets detailed member details.
   * @return A FAST member's details, see `MemberDetails`.
   */
  function detailedMember(address member)
      public view returns(MemberDetails memory) {
    return MemberDetails({
      addr: member,
      balance: LibFastToken.data().balances[member],
      isGovernor: LibHasGovernors.data().governorSet.contains(member)
    });
  }

  function paginateDetailedMembers(uint256 index, uint256 perPage)
      external view returns(MemberDetails[] memory, uint256) {
    LibHasMembers.Data storage membersData = LibHasMembers.data();
    (address[] memory members, uint256 nextCursor) =
      LibPaginate.addresses(membersData.memberSet.values, index, perPage);
    MemberDetails[] memory values = new MemberDetails[](members.length);
    uint256 length = members.length;
    for (uint256 i = 0; i < length;) {
      values[i] = detailedMember(members[i]);
      unchecked { ++i; }
    }
    return (values, nextCursor);
  }
}
","keccak256":"0x583bc2b0d84dedef08b2e7e9fbd38b18aba7f0ec88acee5f63411872754a2f33","license":"MIT"},"contracts/fast/FastHistoryFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastHistory.sol';


/**
 * @notice Although past events could be scrapped from the chain, we want to
 * the frontend to be capable of listing past transfers and minting / burning events.
 * This facet is in charge of performing archival of these things.
 */
contract FastHistoryFacet is AFastFacet {
  /// Minting history-keeping methods.

  /**
   * @notice This method is a callback for other facets to signal whenever new tokens are minted.
   * 
   * Business logic:
   * - Requires that the caller must be another facet.
   * - Adds a supply proof item of type `LibFastHistory.SupplyOp.Mint` on top of the stack.
   */
  function minted(uint256 amount, string calldata ref)
      external onlyDiamondFacet() {
    // Keep track of the mint.
    LibFastHistory.data().supplyProofs.push(
      LibFastHistory.SupplyProof({
        op: LibFastHistory.SupplyOp.Mint,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  /**
   * @notice This method is a callback for other facets to signal whenever new tokens are burnt.
   * 
   * Business logic:
   * - Requires that the caller must be another facet.
   * - Adds a supply proof item of type `LibFastHistory.SupplyOp.Burn` on top of the stack.
   */
  function burnt(uint256 amount, string calldata ref)
      external onlyDiamondFacet() {
    // Keep track of the unmint.
    LibFastHistory.data().supplyProofs.push(
      LibFastHistory.SupplyProof({
        op: LibFastHistory.SupplyOp.Burn,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  /**
   * @notice Returns the number of supply proofs (minting and burning together) ever created.
   * @return A `uint256`.
   */
  function supplyProofCount()
      external view returns(uint256) {
    return LibFastHistory.data().supplyProofs.length;
  }

  /**
   * @notice Returns a page of supply proofs (minting and burning together).
   * @param cursor is the zero-based index where to start fetching records.
   * @param perPage is the number of items to return.
   * @return A `(LibFastHistory.SupplyProof[], uint256)` tuple containing a page of data and the cursor to the next page.
   */
  function paginateSupplyProofs(uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.SupplyProof[] memory, uint256) {
    return LibPaginate.supplyProofs(LibFastHistory.data().supplyProofs, cursor, perPage);
  }

  /// Transfer history-keeping methods.

  /**
   * @notice This method is a callback for other facets to signal whenever a transfer has completed successfuly.
   * 
   * Business logic:
   * - Requires that the caller must be another facet.
   * - Keeps track of the operation in various tracking structures, so that it can be queried later by `sender` and `recipient`.
   * - Pushes a transfer proof to the main transfer proof tracking stack.
   */
  function transfered(address spender, address from, address to, uint256 amount, string calldata ref)
      external onlyDiamondFacet() {
    LibFastHistory.Data storage s = LibFastHistory.data();
    // Keep track of the transfer proof ID for the sender and for the recipient.
    s.transferProofInvolvements[from].push(s.transferProofs.length);
    s.transferProofInvolvements[to].push(s.transferProofs.length);
    // Keep track of the transfer proof globally.
    s.transferProofs.push(
      LibFastHistory.TransferProof({
        spender: spender,
        from: from,
        to: to,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  /**
   * @notice Returns the number of transfer proofs ever created.
   * @return A `uint256`.
   */
  function transferProofCount()
      external view returns(uint256) {
    return LibFastHistory.data().transferProofs.length;
  }

  /**
   * @notice Returns a page of transfer proofs.
   * @param cursor is the zero-based index where to start fetching records.
   * @param perPage is the number of items to return.
   * @return A `(LibFastHistory.TransferProof[], uint256)` tuple containing a page of data and the cursor to the next page.
   */
  function paginateTransferProofs(uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.TransferProof[] memory, uint256) {
    return LibPaginate.transferProofs(LibFastHistory.data().transferProofs, cursor, perPage);
  }

  /**
   * @notice Counts all past inbound and outbound transfers involving a given address.
   * @param involvee is the address for which to get the transfer proofs.
   */
  function transferProofByInvolveeCount(address involvee)
      external view returns(uint256) {
    return LibFastHistory.data().transferProofInvolvements[involvee].length;
  }

  /**
   * @notice Returns pages of indices of past inbound and outbound transfer proofs by involvee.
   * @dev This function is reading from an indexing data structure. Each index points to a record
   * in the main transfer proof storage, and can then be found in `transferProofs` at returned indices.
   * @param involvee is the address for which to retrieve a page of data.
   * @param cursor is where to start.
   * @param perPage is how many records at most should be returned.
  */
  function paginateTransferProofIndicesByInvolvee(address involvee, uint256 cursor, uint256 perPage)
      external view returns(uint256[] memory, uint256) {
    return LibPaginate.uint256s(LibFastHistory.data().transferProofInvolvements[involvee], cursor, perPage);
  }

  /**
   * @notice Returns a page of inbound and outbound transfer proofs based on an involvee.#
   * @param involvee is the address for which to fetch the data.
   * @param cursor is where to start.
   * @param perPage is how many items at most to return.
   * @return A `(LibFastHistory.TransferProof[], uint256)` tuple containing the results and the cursor to the next page.
   */
  function paginateTransferProofsByInvolvee(address involvee, uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.TransferProof[] memory, uint256) {
    LibFastHistory.Data storage s = LibFastHistory.data();
    uint256[] storage collection  = s.transferProofInvolvements[involvee];
    uint256 length = (perPage > collection.length - cursor) ? collection.length - cursor : perPage;
    LibFastHistory.TransferProof[] memory values = new LibFastHistory.TransferProof[](length);
    for (uint256 i = 0; i < length;) {
      values[i] = s.transferProofs[collection[cursor + i]];
      unchecked { ++i; }
    }
    return (values, cursor + length);
  }
}
","keccak256":"0x62822d72ec1fe8073dca0c1853a4fd2e5709c91875fd30416d229c80f5f9de28","license":"MIT"},"contracts/fast/FastTokenFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../interfaces/IERC20.sol';
import '../interfaces/ICustomErrors.sol';
import '../common/AHasMembers.sol';
import '../lib/LibDiamond.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastToken.sol';
import '../marketplace/MarketplaceTokenHoldersFacet.sol';
import './FastTopFacet.sol';
import './FastAccessFacet.sol';
import './FastHistoryFacet.sol';
import './FastFrontendFacet.sol';


contract FastTokenFacet is AFastFacet, IERC20 {
  using LibAddressSet for LibAddressSet.Data;

  /// Constants.

  string internal constant DEAD_TOKENS_RETRIEVAL = 'Dead tokens retrieval';

  /// Minting methods.

  /**
   * @notice Mints an amount of FAST tokens.
   *  A reference can be passed to identify why this happened for example.
   *
   * Business logic:
   * - Modifiers:
   *   - Requires the caller to be a member of the Issuer contract.
   * - Requires that either the token has continuous supply, or that no tokens have been minted yet.
   * - Increases the reserve balance by `amount`.
   * - Calls `FastHistoryFacet.minted`.
   * - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Emits a `Minted(amount, ref)` event.
   * @param amount The number of FAST tokens to mint.
   * @param ref A reference for this minting operation.
   */
  function mint(uint256 amount, string calldata ref)
      external
      onlyIssuerMember {
    LibFastToken.Data storage s = LibFastToken.data();
    // We want to make sure that either of these two is true:
    // - The token doesn't have fixed supply.
    // - The token has fixed supply but has no tokens yet (First and only mint).
    if (FastTopFacet(address(this)).hasFixedSupply() && (s.totalSupply != 0 || this.balanceOf(address(0)) != 0))
      revert ICustomErrors.RequiresContinuousSupply();

    // Prepare the minted amount on the zero address.
    s.balances[address(0)] += amount;

    // Keep track of the minting operation.
    FastHistoryFacet(address(this)).minted(amount, ref);

    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit Minted(amount, ref, msg.sender);
  }

  /**
   * @notice Burns an amount of FAST tokens.
   *  A reference can be passed to identify why this happened for example.
   *
   * Business logic.
   * - Modifiers:
   *   - Requires the caller to be a member of the Issuer contract.
   * - Requires that the token has continuous supply.
   * - Requires that there are enough funds in the reserve to cover for `amount` being burnt.
   * - Decreases the reserve balance by `amount`.
   * - Calls `FastHistoryFacet.burnt(amount, ref)`.
   * - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Emits a `Burnt(amount, ref)`.
   * @param amount The number of FAST tokens to mint.
   * @param ref A reference for this minting operation.
   */
  function burn(uint256 amount, string calldata ref)
      external
      onlyIssuerMember {
    LibFastToken.Data storage s = LibFastToken.data();

    if (FastTopFacet(address(this)).hasFixedSupply())
      revert ICustomErrors.RequiresContinuousSupply();

    // Remove the minted amount from the zero address.
    s.balances[address(0)] -= amount;

    // Keep track of the minting operation.
    FastHistoryFacet(address(this)).burnt(amount, ref);

    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit Burnt(amount, ref, msg.sender);
  }

  /**
   * @notice Allows an Issuer member to move an arbitrary account's holdings back to the reserve,
   * as per regulatory requirements.
   *
   * Business logic:
   * - Modifiers:
   *   - Requires that the caller is a member of the Issuer contract.
   * - If the amount held by `holder` is not zero
   *   - The balance of `holder` should be set to zero.
   *   - The reserve's balance should be increased by how much was on the holder's account.
   *   - Total supply should be decreased by that amount too.
   * - The `holder`'s address should not be tracked as a token holder in this FAST anymore.
   * - The `holder`'s address should not be tracked as a token holder in the Marketplace anymore.
   * - A `Transfer(holder, reserve, amount)` event should be emited.
   * - If the amount previously held by `holder` was not zero,
   *   - Since the reserve balance and total supply have changed, the `FastFrontendFacet.emitDetailsChanged()` function should be called.
   * @param holder is the address for which to move the tokens from.
   */
  function retrieveDeadTokens(address holder)
      external
      onlyIssuerMember {
    // Cache how many tokens the holder has.
    uint256 amount = balanceOf(holder);
    // Note: The amount **can** be zero in this function.

    // Grab a pointer to the token storage.
    LibFastToken.Data storage s = LibFastToken.data();

    // These should only run if the amount is zero, as they result in a no-op.
    if (amount > 0) {
      // Set the holder balance to zero.
      s.balances[holder] = 0;
      // Increment the reserve's balance.
      s.balances[address(0)] += amount;
      // The tokens aren't in circulation anymore - decrease total supply.
      s.totalSupply -= amount;
    }

    // Since the holder's account is now empty, make sure to keep track of it both
    // in this FAST and in the marketplace.
    s.tokenHolders.remove(holder, true);
    MarketplaceTokenHoldersFacet(LibFast.data().marketplace).fastBalanceChanged(holder, 0);

    // Keep track of this transfer in history facet.
    FastHistoryFacet(address(this)).transfered(msg.sender, holder, address(0), amount, DEAD_TOKENS_RETRIEVAL);

    // This operation can be seen as a regular transfer between holder and reserve. Emit.
    emit FastTransfer(msg.sender, holder, address(0), amount, DEAD_TOKENS_RETRIEVAL);
    emit Transfer(holder, address(0), amount);

    // If amount wasn't zero, total supply and reserve balance have changed - emit.
    if (amount > 0)
      FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  // ERC20 implementation and transfer related methods.

  /**
   * @notice The name of this FAST (ERC20 standard).
   * @return string Name of the FAST.
   */
  function name()
      external view returns(string memory) {
    return LibFastToken.data().name;
  }

  /**
   * @notice The symbol of this FAST (ERC20 standard).
   * @return string Symbol of the FAST.
   */
  function symbol()
      external view returns(string memory) {
    return LibFastToken.data().symbol;
  }

  /**
   * @notice The `decimals` of this FAST (ERC20 standard).
   * @return uint256 Number of decimals the FAST has.
   */
  function decimals()
      external view returns(uint256) {
    return LibFastToken.data().decimals;
  }

  /**
   * @notice The total supply of the FAST (ERC20 standard).
   * @return uint256 Total supply of the FAST.
   */
  function totalSupply()
      external override(IERC20) view returns(uint256) {
    return LibFastToken.data().totalSupply;
  }

  /**
   * @notice The balance of the passed owner (ERC20 standard).
   * @param owner The owners address to get the balance of.
   * @return uint256 The current balance of this owner's account.
   */
  function balanceOf(address owner)
      public view override(IERC20) returns(uint256) {
    return LibFastToken.data().balances[owner];
  }

  /**
   * @notice See `performTransfer`, the spender will be equal to the `owner`, and the `ref` will be defauted. */
  function transfer(address to, uint256 amount)
      external override(IERC20) returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: msg.sender,
        to: to,
        amount: amount,
        ref: LibFastToken.DEFAULT_TRANSFER_REFERENCE
      })
    );
    return true;
  }

  /**
   * @notice See `performTransfer`, the spender will be equal to the `owner`. */
  function transferWithRef(address to, uint256 amount, string calldata ref)
      external returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: msg.sender,
        to: to,
        amount: amount,
        ref: ref
      })
    );
    return true;
  }

  function allowance(address owner, address spender)
      public view override(IERC20) returns(uint256) {
    LibFastToken.Data storage s = LibFastToken.data();
    // If the allowance being queried is owned by the reserve, and `spender` is
    // an Issuer member, `spender` owns the full balance of `owner`. If they are
    // not an Issuer member then their allowance is zero. Otherwise, the regular given
    // allowance for `spender` over `owner` applies.
    if (owner == address(0))
      return AHasMembers(LibFast.data().issuer).isMember(spender)
        ? s.balances[owner]
        : 0;
    else
      return s.allowances[owner][spender];
  }

  /**
   * @notice This method directly calls `performApproval`, setting its `from` paramter to the sender of
   * the transaction.
   * @param spender is the address to allow spending from the caller's wallet.
   * @param amount is how much to **increase** the allowance.
   */
  function approve(address spender, uint256 amount)
      external override(IERC20) returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performApproval(msg.sender, spender, amount);
    return true;
  }

  /**
   * @notice This method directly calls `performDisapproval`, setting its `from` parameter to the sender of
   * the transaction.
   * @param spender is the address to disallow spending from the caller's wallet.
   * @param amount is how much to **decrease** the allowance.
   */
  function disapprove(address spender, uint256 amount)
      external
      onlyMember(msg.sender)
      returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performDisapproval(msg.sender, spender, amount);
    return true;
  }

  /// @notice See `performTransfer`, the `ref` will be defaulted.
  function transferFrom(address from, address to, uint256 amount)
      external override(IERC20) returns(bool) {
    transferFromWithRef(from, to, amount, LibFastToken.DEFAULT_TRANSFER_REFERENCE);
    return true;
  }

  /// @notice See `performTransfer`.
  function transferFromWithRef(address from, address to, uint256 amount, string memory ref)
      public returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: from,
        to: to,
        amount: amount,
        ref: ref
      })
    );
    return true;
  }

  // Allowances query operations.

  function givenAllowanceCount(address owner)
      external view returns(uint256) {
    return LibFastToken.data().allowancesByOwner[owner].values.length;
  }

  function paginateAllowancesByOwner(address owner, uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastToken.data().allowancesByOwner[owner].values,
      index,
      perPage
    );
  }

  function receivedAllowanceCount(address spender)
      external view returns(uint256) {
    return LibFastToken.data().allowancesBySpender[spender].values.length;
  }

  function paginateAllowancesBySpender(address spender, uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastToken.data().allowancesBySpender[spender].values,
      index,
      perPage
    );
  }

  // These functions would be internal / private if we weren't using the diamond pattern.
  // Instead, they're `onlyDiamondFacet` - eg can only be called by facets of the current
  // FAST.

  struct TransferArgs {
    address spender;
    address from;
    address to;
    uint256 amount;
    string ref;
  }

  /**
   * @notice This is the internal method that gets called whenever a transfer is initiated. Both `transfer`,
   * `transferWithRef`, and their variants internally call this function.
   *
   * Business logic:
   * - Modifiers:
   *   - Only facets of the current diamond should be able to call this.
   * - Requires that transfers are enabled for this FAST.
   * - Requires that `from` and `to` are different addresses.
   * - Requires that `from` membership is active in the marketplace.
   * - If `from` is not the reserve, requires that `from` is a valid token holder.
   * - If `from` is the reserve, requires that the message sender is an issuer member.
   * - Requires that `to` is a valid token holder.
   * - Requires that the amount is a positive value.
   * - If the transfer is an allowance - e.g. the `spender` is not the same as the `from` address,
   *   - The allowance given by the `from` address to the `spender` covers for the `amount`.
   *     - If we are **not** transfering **from** the reserve,
   *       - Decreases the allowance given by `from` to `spender`.
   *         - If the new allowance reaches zero,
   *           - Stop tracking the allowance in the allowance lookup tables for both spending and receiving directions.
   * - Decreases the balance of the `owner` address.
   * - Increases the balance of the `to` address by `amount`.
   * - If we are **not** transfering **from** the reserve,
   *   - Requires that there are enough transfer credits to cover for `amount`.
   *   - Decreases the transfer credits by `amount`.
   * - If the `to` address is the reserve,
   *   - Decreases the total supply by `amount`.
   *   - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Else, if the `from` address is the reserve,
   *   - Increases the total supply by `amount`.
   *   - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Calls `FastHistoryFacet.transfered`.
   * - Emits a `Transfer(from, to, amount)` event.
   */
  function performTransfer(TransferArgs calldata p)
      external onlyDiamondFacet {
    // TODO: Make this function return instead of raising errors.

    // Grab a pointer to our top-level storage.
    LibFast.Data storage topData = LibFast.data();

    // Requires that transfers are enabled for this FAST.
    if (FastTopFacet(address(this)).transfersDisabled())
      revert ICustomErrors.RequiresTransfersEnabled();
    // Requires that `from` and `to` are different addresses.
    else if (p.from == p.to)
      revert ICustomErrors.RequiresDifferentSenderAndRecipient(p.from);
    // Requires that allowance transfers from the reserve are performed by issuer members only.
    else if (p.from == address(0) && !AHasMembers(topData.issuer).isMember(p.spender))
      revert ICustomErrors.RequiresIssuerMembership(p.spender);

    // Requires that the `from` address can hold tokens.
    else if (!canHoldTokens(p.from))
      revert ICustomErrors.RequiresValidTokenHolder(p.from);
    // Requires that the `from` address marketplace membership is active if not the reserve.
    else if (p.from != address(0) && !IHasActiveMembers(LibFast.data().marketplace).isActiveMember(p.from))
      revert ICustomErrors.RequiresMarketplaceActiveMembership(p.from);

    // Requires that the `to` address can hold tokens.
    else if (!canHoldTokens(p.to))
      revert ICustomErrors.RequiresValidTokenHolder(p.to);

    // For any non-zero amount, update balances and allowances, notify other contracts, etc.
    if (p.amount != 0) {
      // Grab a pointer to our token storage.
      LibFastToken.Data storage tokenData = LibFastToken.data();

      // If this is an allowance transfer and if the `from` account is not the reserve...
      if (p.spender != p.from && p.from != address(0)) {
        // Decrease allowance.
        uint256 newAllowance = tokenData.allowances[p.from][p.spender] -= p.amount;
        // If the allowance reached zero, we want to remove that allowance from
        // the various other places where we keep track of it.
        if (newAllowance == 0) {
          tokenData.allowancesByOwner[p.from].remove(p.spender, true);
          tokenData.allowancesBySpender[p.spender].remove(p.from, true);
        }
      }

      // Keep track of the balances - `from` spends (decrease), `to` receives (increase).
      uint256 fromBalance = (tokenData.balances[p.from] -= p.amount);
      uint256 toBalance = (tokenData.balances[p.to] += p.amount);

      // Keep track of who has what FAST.
      MarketplaceTokenHoldersFacet(topData.marketplace).fastBalanceChanged(p.from, fromBalance);
      MarketplaceTokenHoldersFacet(topData.marketplace).fastBalanceChanged(p.to, toBalance);

      // Keep track of who holds this token.
      balanceChanged(p.from, fromBalance);
      balanceChanged(p.to, toBalance);

      // If the funds are going to the reserve...
      if (p.to == address(0)) {
        // Decrease total supply.
        tokenData.totalSupply -= p.amount;
        // Emit a top-level details change event.
        FastFrontendFacet(address(this)).emitDetailsChanged();
      }
      // If the funds are moving from the zero address...
      else if (p.from == address(0)) {
        // Increase total supply.
        tokenData.totalSupply += p.amount;
        // Emit a top-level details change event.
        FastFrontendFacet(address(this)).emitDetailsChanged();
      }
    }

    // Keep track of the transfer in the history facet.
    FastHistoryFacet(address(this)).transfered(p.spender, p.from, p.to, p.amount, p.ref);

    // Emit!
    emit FastTransfer(p.spender, p.from, p.to, p.amount, p.ref);
    emit Transfer(p.from, p.to, p.amount);
  }

  /**
   * @notice Increases the allowance given by `from` to `spender` by `amount`.
   * Note that this function should run and emit even if the amount passed is zero.
   * Business logic:
   * - Modifiers:
   *   - Only facets of the current diamond should be able to call this.
   *   - Requires that `onlyTokenHolder` passes for the `from` address.
   * - Requires that the `amount` is positive number.
   * - Increases the allowance given by `from` to `spender` by `amount`.
   * - Update the allowance lookup tables in both directions.
   * - Emits an `Approval(from, spender, amount)`.
   * @param from is the wallet from which to give the allowance.
   * @param spender is the receiver of the allowance.
   * @param amount is how much to **increase** the current allowance by.
   * 
   * Note: This function runs when amount is zero, and will emit.
   */
  function performApproval(address from, address spender, uint256 amount)
      external
      onlyDiamondFacet {
    // Allowance cannot be given over the reserve.
    if (from == address(0))
      revert ICustomErrors.UnsupportedOperation();
    // Require that the `from` address can hold tokens.
    else if (!canHoldTokens(from))
      revert ICustomErrors.RequiresValidTokenHolder(from);

    if (amount > 0) {
      LibFastToken.Data storage s = LibFastToken.data();
      // Note that we are not exactly following ERC20 here - we don't want to **set** the allowance to `amount`
      // to mitigate a possible attack.
      // See https://docs.google.com/document/d/1YLPtQxZu1UAvO9cZ1O2RPXBbT0mooh4DYKjA_jp-RLM/edit#heading=h.gmr6zdg47087.
      s.allowances[from][spender] += amount;
    // Keep track of given and received allowances.
      s.allowancesByOwner[from].add(spender, true);
      s.allowancesBySpender[spender].add(from, true);
    }

    // Emit!
    emit Approval(from, spender, amount);
  }

  /**
   * @notice Decreases allowance given by `from` to `spender` by `amount`.
   *
   * Business logic:
   * - Modifiers:
   *   - Only facets of the current diamond should be able to call this.
   * - The allowance given by `from` to `spender` is decreased by `amount`.
   * - Whether the allowance reached zero, stop tracking it by owner and by spender.
   * - Emit a `Disapproval(from, spender, amount)` event.
   * 
   * Note: This function runs when amount is zero, and will emit.
   */
  function performDisapproval(address from, address spender, uint256 amount)
      external
      onlyDiamondFacet {
    if (amount != 0) {
      LibFastToken.Data storage s = LibFastToken.data();

      // Remove allowance.
      s.allowances[from][spender] -= amount;

      // Whenever the allowance reaches zero, stop tracking it by owner and spender.
      if (s.allowances[from][spender] == 0) {
        s.allowancesByOwner[from].remove(spender, true);
        s.allowancesBySpender[spender].remove(from, true);
      }
    }

    // Emit!
    emit Disapproval(from, spender, amount);
  }

  // WARNING: This method contains two loops. We know that this should never
  // happen in solidity. However:
  // - In the context of our private chain, gas is cheap.
  // - It can only be called by a governor.
  function beforeRemovingMember(address member)
      external onlyDiamondFacet() {
    if (balanceOf(member) != 0)
      revert ICustomErrors.RequiresPositiveBalance(member);

    LibFastToken.Data storage s = LibFastToken.data();

    // Remove all given allowances.
    {
      address[] storage gaData = s.allowancesByOwner[member].values;
      while (gaData.length > 0) {
        // Make sure the call is performed externally so that we can mock.
        address spender = gaData[0];
        this.performDisapproval(member, spender, s.allowances[member][spender]);
      }
    }

    // Remove all received allowances.
    {
      address[] storage raData = s.allowancesBySpender[member].values;
      while (raData.length > 0) {
        // Make sure the call is performed externally so that we can mock.
        address owner = raData[0];
        this.performDisapproval(owner, member, s.allowances[owner][member]);
      }
    }
  }

  function holders()
      external view
      returns(address[] memory) {
    LibFastToken.Data storage s = LibFastToken.data();
    return s.tokenHolders.values;
  }

  function balanceChanged(address holder, uint256 balance)
      private {
    // Return early if this is the zero address.
    if (holder == address(0))
      return;

    LibFastToken.Data storage s = LibFastToken.data();

    // If this is a positive balance and it doesn't already exist in the set, add address.
    if (balance > 0 && !s.tokenHolders.contains(holder))
      s.tokenHolders.add(holder, false);
    // If the balance is 0 and it exists in the set, remove it.
    else if (balance == 0 && s.tokenHolders.contains(holder))
      s.tokenHolders.remove(holder, false);
  }

  // Private and helper methods.

  /**
   * @notice Ensures that the given address is a member of the current FAST or the Zero Address.
   *
   * Business logic:
   *   - If the candidate is the reserve, it is a valid token holder.
   *   - If the FAST is semi-public,
   *     - We require that candidate is a member of the Marketplace contract and is active in it.
   *   - Otherwise,
   *     - Require that the candidate is a member of the FAST.
   * @param candidate The address to check.
   * @return A boolean set to `true` if `candidate` can hold tokens, `false` otherwise.
   */
  function canHoldTokens(address candidate)
      private view returns(bool) {
    // Zero address can hold tokens, in any cases.
    if (candidate == address(0))
      return true;
    // If the FAST is semi public, any member of the marketplace can hold tokens.
    else if (FastTopFacet(address(this)).isSemiPublic()) {
      return AHasMembers(LibFast.data().marketplace).isMember(candidate);
    }
    // FAST is private, only members of the fast can hold tokens.
    else {
      return AHasMembers(address(this)).isMember(candidate);
    }
  }
}
","keccak256":"0x5b2eda3ed2e86968070f171acc9b521e26c349121a609717c872b6757bffaef3","license":"MIT"},"contracts/fast/FastTopFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibHelpers.sol';
import './lib/AFastFacet.sol';
import './lib/LibFast.sol';
import './lib/IFastEvents.sol';
import './FastFrontendFacet.sol';
import './FastTokenFacet.sol';


contract FastTopFacet is AFastFacet {
  // Getters and setters for global flags.

  /**
   * @notice Get the Issuer address.
   * @return address Address of Issuer.
   */
  function issuerAddress()
      external view returns(address) {
    return LibFast.data().issuer;
  }

  /**
   * @notice Get the Marketplace address.
   * @return address Address of Marketplace.
   */
  function marketplaceAddress()
      external view returns(address) {
    return LibFast.data().marketplace;
  }

  /**
   * @notice Is this FAST a semi public FAST?
   * @return bool Yes/no semi public.
   */
  function isSemiPublic()
      external view returns(bool) {
    return LibFast.data().isSemiPublic;
  }

  /**
   * @notice Is this FAST a fixed supply FAST?
   * @return bool Yes/no fixed supply.
   */
  function hasFixedSupply()
      external view returns(bool) {
    return LibFast.data().hasFixedSupply;
  }

  /**
   * @notice Are transfers enabled across this FAST?
   * @return boolean `true` if transfers are disabled, `false` if transfers are enabled.
   */
  function transfersDisabled()
      external view returns(bool) {
    return LibFast.data().transfersDisabled;
  }

  // Setters for global flags.

  /**
   * @notice Allows to switch from a private scheme to a semi-public scheme,
   *  but not the other way around, unless the total supply is zero.
   * @param flag Set the semi public flag to true/false.
   */
  function setIsSemiPublic(bool flag)
      external
      onlyIssuerMember {
    // Someone is trying to toggle back to private?... No can do!
    if (this.isSemiPublic() && FastTokenFacet(address(this)).totalSupply() != 0) {
      revert ICustomErrors.UnsupportedOperation();
    }
    LibFast.data().isSemiPublic = flag;
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  /**
   * @notice Allows an issuer member to enable or disable all transfers within this FAST.
   * @param flag Set the transfer capability to active or not.
   */
  function setTransfersDisabled(bool flag)
      external
      onlyIssuerMember {
    LibFast.Data storage d = LibFast.data();
    // Only make changes and emit if the new flag is different than the old one.
    if (d.transfersDisabled != flag) {
      // Set flag.
      d.transfersDisabled = flag;
      // Emit!
      FastFrontendFacet(address(this)).emitDetailsChanged();
    }
  }

  /**
   * @notice Retrieves the group slug to which this FAST belongs.
   * @return string The group slug string.
   */
  function group()
      external view returns(string memory) {
    return LibFast.data().group;
  }

  /**
   * @notice Assigns the FAST into a group given its slug.
   * It should only be callable by the Issuer contract.
   * @param newGroup is the slug for the new group for this FAST.
   */
  function setGroup(string calldata newGroup)
      external onlyIssuerContract {
    // Set group slug.
    LibFast.data().group = newGroup;
  }
}
","keccak256":"0xd239f91a572a8df989e2e9ca163b29a3abc86a205c003c5c301630a49d11bd8c","license":"MIT"},"contracts/fast/lib/AFastFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../../lib/LibHelpers.sol';
import '../../lib/LibAddressSet.sol';
import '../../common/AHasGovernors.sol';
import '../../common/AHasMembers.sol';
import '../../common/AHasAutomatons.sol';
import '../../interfaces/IHasActiveMembers.sol';
import '../lib/LibFast.sol';
import './IFastEvents.sol';


/**
 * @title Abstract FAST helper contract.
 * @notice This abstract contract encapsulates modifiers allowing inheriting facets to guard against
 * certain permissions.
 */
abstract contract AFastFacet is IFastEvents {
  using LibAddressSet for LibAddressSet.Data;

  /// Internal ACL functions.

  function _isMarketplaceMember(address who)
      internal view returns(bool) {
    return AHasMembers(LibFast.data().marketplace).isMember(who);
  }

  function _isMarketplaceActiveMember(address who)
      internal view returns(bool) {
    return IHasActiveMembers(LibFast.data().marketplace).isActiveMember(who);
  }

  function _isIssuerMember(address who)
      internal view returns(bool) {
    return AHasMembers(LibFast.data().issuer).isMember(who);
  }

  /// Modifiers.

  /// @notice Ensures that a method can only be called by another facet of the same diamond.
  modifier onlyDiamondFacet() {
    if (!LibHelpers._isDiamondFacet(msg.sender))
      revert ICustomErrors.InternalMethod();
    _;
  }

  /// @notice Ensures that a method can only be called by the owner of this diamond.
  modifier onlyDiamondOwner() {
    if (!LibHelpers._isDiamondOwner(msg.sender))
      revert ICustomErrors.RequiresDiamondOwnership(msg.sender);
    _;
  }

  /// @notice Ensures that a method can only be called by the singleton deployer contract factory.
  modifier onlyDeployer() {
    if (!LibHelpers._isDeployer(msg.sender))
      revert ICustomErrors.InternalMethod();
    _;
  }

  /// @notice Ensures that a method can only be called by the issuer contract.
  modifier onlyIssuerContract() {
    if (msg.sender != LibFast.data().issuer)
      revert ICustomErrors.InternalMethod();
    _;
  }

  /**
   * @notice Ensures that the given address is a member of the Marketplace.
   * @param who The address to check.
   */
  modifier onlyMarketplaceMember(address who) {
    if (!_isMarketplaceMember(who))
      revert ICustomErrors.RequiresMarketplaceMembership(who);
    _;
  }

  /**
   * @notice Ensures a who is an active member of the Marketplace.
   * @param who The address to check.
   */
  modifier onlyMarketplaceActiveMember(address who) {
    if (!_isMarketplaceActiveMember(who))
      revert ICustomErrors.RequiresMarketplaceActiveMembership(who);
    _;
  }

  /**
   * @notice Ensures that the message sender is a member of the Issuer.
   */
  modifier onlyIssuerMember() {
    if (!_isIssuerMember(msg.sender))
      revert ICustomErrors.RequiresIssuerMembership(msg.sender);
    _;
  }

  /**
   * @notice Ensures that the given address is a governor of the FAST.
   * @param who The address to check.
   */
  modifier onlyGovernor(address who) {
    if (!AHasGovernors(address(this)).isGovernor(who))
      revert ICustomErrors.RequiresFastGovernorship(who);
    _;
  }

  /**
   * @notice Ensures that the given address is a member of the FAST.
   * @param who The address to check.
   */
  modifier onlyMember(address who) {
    if (!AHasMembers(address(this)).isMember(who))
      revert ICustomErrors.RequiresFastMembership(who);
    _;
  }

  /**
   * @notice Ensures address `a` and `b` are different.
   * @param a Address a
   * @param b Address b
   */
  modifier differentAddresses(address a, address b) {
    if (a == b)
      revert ICustomErrors.RequiresDifferentSenderAndRecipient(a);
    _;
  }
}
","keccak256":"0x1ac08a2fdd4a038dbbf23720a2b13516c4fb789e3fd9aa6c362fc74649daf0d6","license":"MIT"},"contracts/fast/lib/IFastEvents.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../../fast/Distribution.sol';
import '../../fast/Crowdfund.sol';


/**
 * @title FAST events inventory.
 * @notice An interface allowing to use events within the Diamond pattern without name colisions.
 * @dev The idea is that as several facets can emit the same events, we don't want to have to re-declare
 * the same event several time. This interface is a per-diamond central place for such event declaration.
 */
interface IFastEvents {
  /// Issuance related events.

  /**
   * @notice Emited whenever an issuance happens in a FAST.
   * @param amount is the amount of tokens that have been minted.
   * @param ref is the reference associated with the minting operation.
   * @param who is the account from which the minting operation originated.
   */
  event Minted(uint256 indexed amount, string indexed ref, address indexed who);
  /**
   * @notice Emited whenever an burning happens in a FAST.
   * @param amount is the amount of tokens that have been burnt.
   * @param ref is the reference associated with the burning operation.
   * @param who is the account from which the burning operation originated.
   */
  event Burnt(uint256 indexed amount, string indexed ref, address indexed who);

  /// Transfer and ERC20 stuff.

  /// @notice See `ERC20.Transfer`.
  event Transfer(address indexed from, address indexed to, uint256 value);
  /// @notice See `ERC20.Approval`.
  event Approval(address indexed owner, address indexed spender, uint256 value);
  /// @notice See `ERC20.Disapproval`.
  event Disapproval(address indexed owner, address indexed spender, uint256 value);
  /**
   * @notice As we augmented the ERC20 standard with a few concepts, we emit our custom events
   * in addition to the ERC20 ones.
   * @param spender is the account who performed the transfer.
   * @param from is the account from which the tokens will be debited from.
   * @param to is the account to which the tokens will be credited to.
   * @param value is the amount of tokens transfered.
   * @param ref is the optional reference associated with the transfer.
   */
  event FastTransfer(address indexed spender, address indexed from, address indexed to, uint256 value, string ref);

  /// Distribution related events.

  event DistributionDeployed(Distribution indexed distribution);
  event DistributionRemoved(Distribution indexed distribution);

  /// Crowdfund related events.

  event CrowdfundDeployed(Crowdfund indexed crowdfund);
  event CrowdfundRemoved(Crowdfund indexed crowdfund);

  /// General events.

  /**
   * @notice This is an event that is fired whenever any of some of the FAST parameters
   * change, so that the frontend can react to it and refresh the general header
   * for that fast as well as the baseball cards in the FASTs list.
   * @param transfersDisabled marks whether or not transfers are disabled by an issuer member at FAST level.
   * @param memberCount is the number of members in the FAST.
   * @param governorCount is the number of governors in the FAST.
   * @param totalSupply is the amount of tokens in circulation in the FAST.
   * @param reserveBalance is the balance of the zero-address (aka reserve) for the FAST.
   */
  event DetailsChanged(
    bool transfersDisabled,
    uint256 memberCount,
    uint256 governorCount,
    uint256 totalSupply,
    uint256 reserveBalance
  );
}
","keccak256":"0x6e837767db68e34177c4dee38ca8827ed270d4cd4c04d1ccb839bafc584cf6dc","license":"MIT"},"contracts/fast/lib/LibFast.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;


/**
 * @notice Top-level shared functionality for FAST diamonds.
 * @dev Note that if you feel like a method should be created inside this library, you might want to really consider
 * whether or not it is the right place for it. Any facet using a method from internal libraries see their bytecode
 * size increase, kind of defeating the benefits of using facets in the first place. So please keep it reasonable. 
 */
library LibFast {
  /// @notice The current version of the storage.
  uint16 internal constant STORAGE_VERSION = 1;
  /// @notice This is keccak256('Fast.storage'):
  bytes32 internal constant STORAGE_SLOT = 0x80c187ea6f955fd624c41fb7a18011cc87d98c6f4c06d897b59142f65707e705;

  // Data structures.

  /**
   * @notice The top-level data structure required for operating any given FAST diamond.
   * @dev The `version` field is used to ensure that storage is at a known version during upgrades.
   */
  struct Data {
    /// @notice The latest intializer version that was called.
    uint16 version;
    /// @notice The internal pointer to the Issuer contract.
    address issuer;
    /// @notice The internal pointer to the Marketplace contract.
    address marketplace;
    /// @notice We have to track whether the token facet provides continuous minting or fixed supply.
    bool hasFixedSupply;
    /// @notice Whether or not this FAST requires to be a member to hold tokens.
    bool isSemiPublic;
    /// @notice A flag which when toggled to `true` disables all transfers across this FAST.
    bool transfersDisabled;
    /// @notice To which FAST group this FAST belongs to, if any.
    string group;
  }

  /**
   * @notice Returns the top-level storage for the calling FAST.
   * @return s a struct pointer for top-level FAST data storage.
   */
  function data()
      internal pure returns(Data storage s) {
    assembly {s.slot := STORAGE_SLOT}
  }
}
