pragma solidity 0.8.10;

import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import '../lib/LibHelpers.sol';
import '../common/AHasMembers.sol';
import '../interfaces/ICustomErrors.sol';
import '../fast/FastTopFacet.sol';
import '../fast/FastTokenFacet.sol';
import './lib/AIssuerFacet.sol';
import './lib/LibIssuerAccess.sol';
import './lib/IIssuerEvents.sol';
import '../issuer/IssuerTopFacet.sol';


contract IssuerAccessFacet is AIssuerFacet, AHasMembers {
  using LibAddressSet for LibAddressSet.Data;
  /// AHasMembers implementation.

  function isMembersManager(address who)
      internal view override(AHasMembers) returns(bool) {
    return AHasMembers(address(this)).isMember(who);
  }

  function isValidMember(address who)
      internal pure override(AHasMembers) returns(bool) {
    return who != LibHelpers.ZERO_ADDRESS;
  }

  /// Membership management.

  /** @notice Callback from FAST contracts allowing the Issuer contract to keep track of governorships.
   * @param governor The governor added to a FAST.
   */
  function governorAddedToFast(address governor)
      external {
    // Verify that the given address is in fact a registered FAST contract.
    if (!IssuerTopFacet(address(this)).isFastRegistered(msg.sender)) {
      revert ICustomErrors.RequiresFastContractCaller();
    }
    // Keep track of the governorship.
    LibIssuerAccess.data().fastGovernorships[governor].add(msg.sender, false);

    emit GovernorshipAdded(msg.sender, governor);
  }

  /** @notice Callback from FAST contracts allowing the Issuer contract to keep track of governorships.
   * @param governor The governor removed from a FAST.
   */
  function governorRemovedFromFast(address governor)
      external {
    // Verify that the given address is in fact a registered FAST contract.
    if (!IssuerTopFacet(address(this)).isFastRegistered(msg.sender)) {
      revert ICustomErrors.RequiresFastContractCaller();
    }
    // Remove the tracked governorship.
    LibIssuerAccess.data().fastGovernorships[governor].remove(msg.sender, false);

    emit GovernorshipRemoved(msg.sender, governor);
  }

  /** @notice Returns a list of FASTs that the passed address is a governor of.
   * @param governor is the address to check governorships of.
   * @param cursor is the index at which to start.
   * @param perPage is how many records should be returned at most.
   * @return A `address[]` list of values at most `perPage` big.
   * @return A `uint256` index to the next page.
   */
  function paginateGovernorships(address governor, uint256 cursor, uint256 perPage)
      external view
      returns(address[] memory, uint256) {
    return LibPaginate.addresses(LibIssuerAccess.data().fastGovernorships[governor].values, cursor, perPage);
  }
}
","keccak256":"0xc36a324bd72efb0c882dba16a69c1bd9b4cbdb12dd1835f0b7d2d647edc2fa26","license":"MIT"},"contracts/issuer/IssuerTopFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../interfaces/ICustomErrors.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import '../lib/LibHelpers.sol';
import '../fast/FastTopFacet.sol';
import '../fast/FastTokenFacet.sol';
import './lib/AIssuerFacet.sol';
import './lib/LibIssuer.sol';

import '@openzeppelin/contracts/utils/math/Math.sol';


contract IssuerTopFacet is AIssuerFacet {
  using LibAddressSet for LibAddressSet.Data;
  // FAST management related methods.

  /**
   * @notice Queries whether a given address is a known and registered FAST contract.
   * @param fast The address of the contract to check.
   * @return A boolean.
   */
  function isFastRegistered(address fast)
      external view returns(bool) {
    return LibIssuer.data().fastSet.contains(fast);
  }

  /**
   * @notice Allows to retrieve the address of a FAST diamond given its symbol.
   * @param symbol The symbol of the FAST diamond to get the address of.
   * @return The address of the corresponding FAST diamond, or the Zero Address if not found.
   */
  function fastBySymbol(string calldata symbol)
      external view returns(address) {
    return LibIssuer.data().fastSymbols[symbol];
  }

  /**
   * @notice Allows the registration of a given FAST diamond with this Issuer.
   * @param fast The address of the FAST diamond to be registered.
   * @notice Requires that the caller is a member of this Issuer.
   * @notice Emits a `FastRegistered` event.
   */
  function registerFast(address fast)
      external
      onlyMember(msg.sender) {
    LibIssuer.Data storage s = LibIssuer.data();
    string memory symbol = FastTokenFacet(fast).symbol();
    if (s.fastSymbols[symbol] != address(0)) {
      revert ICustomErrors.DuplicateEntry();
    }

    // Add the FAST to our list.
    s.fastSet.add(fast, false);
    // Add the fast symbol to our list.
    s.fastSymbols[symbol] = fast;

    // Emit!
    emit FastRegistered(fast);
  }

  /**
   * @notice Allows the unregistration of a given FAST diamond with this Issuer.
   * @param fast The address of the FAST diamond to be unregistered.
   * @notice Requires that the caller is a member of this Issuer.
   * @notice Emits a `FastUnregistered` event.
   */
  function unregisterFast(address fast)
    external
    onlyMember(msg.sender) {
      LibIssuer.Data storage s = LibIssuer.data();
      string memory symbol = FastTokenFacet(fast).symbol();

      // Disable transfers in the FAST.
      FastTopFacet(fast).setTransfersDisabled(true);

      // Remove the FAST from our lists.
      s.fastSet.remove(fast, false);
      delete s.fastSymbols[symbol];

      // Emit!
      emit FastUnregistered(fast);
    }

  /**
   * @notice Counts the number of FAST diamonds registered with this Issuer.
   * @return The number of FAST diamonds registered with this Issuer.
   */
  function fastCount()
      external view returns(uint256) {
    return LibIssuer.data().fastSet.values.length;
  }

  /**
   * @notice Paginates the FAST diamonds registered with this Issuer based on a starting cursor and a number of records per page.
   * @param cursor The index at which to start.
   * @param perPage How many records should be returned at most.
   * @return A `address[]` list of values at most `perPage` big.
   * @return A `uint256` index to the next page.
   */
  function paginateFasts(uint256 cursor, uint256 perPage)
      external view
      returns(address[] memory, uint256) {
    return LibPaginate.addresses(LibIssuer.data().fastSet.values, cursor, perPage);
  }

  /**
   * @notice Allows an issuer member to move collected ERC20 fees from this contract
   * to an arbitrary address.
   * @param token is the address of the ERC20 to be collected.
   * @param amount is how much should be moved from the ERC20 to the collection address.
   * @param to is the collection address - eg the address that will receive the ERC20 tokens.
   */
  function transferERC20Tokens (IERC20 token, uint256 amount, address to)
      public
      onlyMember(msg.sender) {
    require(token.transfer(to, amount));
  }

  /**
   * @notice Allows to assign FASTs to groups.
   * @param fast The FAST address to assign.
   * @param newGroup The name of the group in which the FAST should be placed.
   */
  function setFastGroup(address fast, string calldata newGroup)
      external onlyMember(msg.sender) {
    string memory oldGroup = FastTopFacet(fast).group();
    // Remove FAST from old group.
    if (bytes(oldGroup).length > 0)
      LibIssuer.data().fastGroups[oldGroup].remove(fast, false);
    // Add FAST to new group.
    if (bytes(newGroup).length > 0)
      LibIssuer.data().fastGroups[newGroup].add(fast, false);
    // Set group name at FAST level.
    FastTopFacet(fast).setGroup(newGroup);
    // Emit!
    emit FastGroupChanged(fast, oldGroup, newGroup);
  }

  /**
   * @notice Paginates the FAST diamonds registered with this Issuer based on a group, starting cursor and a number of records per page.
   * @param group The group to paginate.
   * @param cursor The index at which to start.
   * @param perPage How many records should be returned at most.
   * @return A `address[]` list of values at most `perPage` big.
   * @return A `uint256` index to the next page.
   */
  function paginateFastsInGroup(string calldata group, uint256 cursor, uint256 perPage)
      external view
      returns(address[] memory, uint256) {
    return LibPaginate.addresses(LibIssuer.data().fastGroups[group].values, cursor, perPage);
  }
}
","keccak256":"0x3e4a9577d738712b87581998d6d727ab4308ebd783590ba6a00f0308aa43922b","license":"MIT"},"contracts/issuer/lib/AIssuerFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../../lib/LibHelpers.sol';
import '../../common/AHasMembers.sol';
import '../../interfaces/ICustomErrors.sol';
import './IIssuerEvents.sol';


/**
* @notice This abstract contract encapsulates modifiers allowing inheriting facets to guard against
* certain permissions.
*/
abstract contract AIssuerFacet is IIssuerEvents {
  /// Modifiers.

  /// @notice Ensures that a method can only be called by another facet of the same diamond.
  modifier onlyDiamondFacet() {
    if (!LibHelpers._isDiamondFacet(msg.sender))
      revert ICustomErrors.InternalMethod();
    _;
  }

  /// @notice Ensures that a method can only be called by the owner of this diamond.
  modifier onlyDiamondOwner() {
    if (!LibHelpers._isDiamondOwner(msg.sender))
      revert ICustomErrors.RequiresDiamondOwnership(msg.sender);
    _;
  }

  /// @notice Ensures that a method can only be called by the singleton deployer contract factory.
  modifier onlyDeployer() {
    if (!LibHelpers._isDeployer(msg.sender))
      revert ICustomErrors.InternalMethod();
    _;
  }

  /**
   * @notice Ensures that the given address is a member of the FAST.
   * @param who The address to check.
   */
  modifier onlyMember(address who) {
    if (!AHasMembers(address(this)).isMember(who))
      revert ICustomErrors.RequiresIssuerMembership(who);
    _;
  }
}
","keccak256":"0x758f6b3cbd8a8aeadf7bd70915b4dbde203d94488d1ca4f84e2c571459739fe9","license":"MIT"},"contracts/issuer/lib/IIssuerEvents.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;


// WARNING: These events must be maintained 1:1 with LibIssuerEvents!
// They also should never be emitted directly, they only help us defining
// typescript types!
interface IIssuerEvents {
  /// FAST registration events.

  /**
   * @notice Emited when a new FAST is registered.
   * @param fast The address of the newly registered FAST diamond.
   */
  event FastRegistered(address indexed fast);
  /**
   * @notice Emited when a FAST is removed from the Issuer contract.
   * @param fast The address of the unregistered FAST.
   */
  event FastUnregistered(address indexed fast);

  /// FAST groupping events.

  event FastGroupChanged(address indexed fast, string indexed oldGroup, string indexed newGroup);

  /// Governors.

  event GovernorshipRemoved(address indexed fast, address indexed governor);
  event GovernorshipAdded(address indexed fast, address indexed governor);
}
