","keccak256":"0x3ea65149b8f82fa95e1efdfc22e9d1e05ed404d595b408dc4cd7c42df190d303","license":"MIT"},"contracts/marketplace/MarketplaceTokenHoldersFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import './lib/AMarketplaceFacet.sol';
import './lib/LibMarketplaceTokenHolders.sol';
import '../issuer/IssuerTopFacet.sol';
import '../interfaces/IERC20.sol';
import '../interfaces/ICustomErrors.sol';

/** @dev The Marketplace FAST balances facet.
 */
contract MarketplaceTokenHoldersFacet is AMarketplaceFacet {
  using LibAddressSet for LibAddressSet.Data;

  /** @dev The callback used when a balance changes on a FAST.
   */
  function fastBalanceChanged(address account, uint256 balance)
    external {
    // Return early if this is the zero address.
    if (account == address(0)) {
      return;
    }

    // Verify that the given address is in fact a registered FAST contract.
    if (!IssuerTopFacet(LibMarketplace.data().issuer).isFastRegistered(msg.sender)) {
      revert ICustomErrors.RequiresFastContractCaller();
    }

    // Get the storage pointer and balance of the token holder.
    LibMarketplaceTokenHolders.Data storage s = LibMarketplaceTokenHolders.data();

    // If this is a positive balance and it doesn't already exist in the set, add address.
    if (balance > 0 && !s.fastHoldings[account].contains(msg.sender)) {
      s.fastHoldings[account].add(msg.sender, false);
    }
    // If the balance is 0 and it exists in the set, remove it.
    else if (balance == 0 && s.fastHoldings[account].contains(msg.sender)) {
      s.fastHoldings[account].remove(msg.sender, false);
    }
  }

  /** @dev A way to get a list of FASTs for an account.
   *  @return list of FAST addresses.
   */
  function holdings(address account)
    external view
    returns(address[] memory) {
    LibMarketplaceTokenHolders.Data storage s = LibMarketplaceTokenHolders.data();
    return s.fastHoldings[account].values;
  }
}
","keccak256":"0x30acbf3f2be497c90d7f5904a734689c60abed7e9d347009953aeb500b88c0dd","license":"MIT"},"contracts/marketplace/lib/AMarketplaceFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../../lib/LibHelpers.sol';
import '../../common/AHasMembers.sol';
import '../../interfaces/ICustomErrors.sol';
import '../lib/LibMarketplace.sol';
import './IMarketplaceEvents.sol';


/**
* @notice This contract is a group of modifiers that can be used by any Marketplace facets to guard against
*       certain permissions.
*/
abstract contract AMarketplaceFacet is IMarketplaceEvents {
  /// Internal ACL functions.

  function _isIssuerMember(address who)
      internal view returns(bool) {
    return AHasMembers(LibMarketplace.data().issuer).isMember(who);
  }

  // Modifiers.

  /// @notice Ensures that a method can only be called by the singleton deployer contract factory.
  modifier onlyDeployer()
      virtual {
    if (!LibHelpers._isDeployer(msg.sender))
      revert ICustomErrors.InternalMethod();
    _;
  }

  /**
   * @notice Requires that the message sender is a member of the linked Issuer.
   */
  modifier onlyIssuerMember()
      virtual {
    if (!_isIssuerMember(msg.sender))
      revert ICustomErrors.RequiresIssuerMembership(msg.sender);
    _;
  }

  /**
   * @notice Requires that the given address is a member of the marketplace.
   * @param who is the address to be checked.
   */
  modifier onlyMember(address who)
      virtual {
    if (!AHasMembers(address(this)).isMember(who))
      revert ICustomErrors.RequiresMarketplaceMembership(who);
    _;
  }
}
